import React, { useState, useRef, useEffect } from 'react';
import { Search, X } from 'lucide-react';

interface FundSearchProps {
  onSearch: (searchText: string) => void;
}

const FundSearch: React.FC<FundSearchProps> = ({ onSearch }) => {
  const [searchText, setSearchText] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const searchTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Debounced search - search after user stops typing for 500ms
  useEffect(() => {
    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }

    if (searchText.trim()) {
      setIsSearching(true);
      searchTimeoutRef.current = setTimeout(() => {
        onSearch(searchText);
        setIsSearching(false);
      }, 500);
    } else {
      onSearch('');
      setIsSearching(false);
    }

    return () => {
      if (searchTimeoutRef.current) {
        clearTimeout(searchTimeoutRef.current);
      }
    };
  }, [searchText, onSearch]);

  const handleClear = () => {
    setSearchText('');
    onSearch('');
  };

  return (
    <div className="relative">
      <div className="relative">
        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
          <Search className="h-5 w-5 text-gray-400" />
        </div>
        <input
          type="text"
          value={searchText}
          onChange={(e) => setSearchText(e.target.value)}
          placeholder="Search for mutual funds..."
          className="block w-full pl-10 pr-10 py-3 border border-gray-300 rounded-lg leading-5 bg-white placeholder-gray-500 focus:outline-none focus:placeholder-gray-400 focus:ring-2 focus:ring-blue-500 focus:border-blue-500 text-sm"
        />
        {searchText && (
          <div className="absolute inset-y-0 right-0 pr-3 flex items-center">
            <button
              onClick={handleClear}
              className="text-gray-400 hover:text-gray-600 focus:outline-none"
            >
              <X className="h-5 w-5" />
            </button>
          </div>
        )}
        {isSearching && (
          <div className="absolute inset-y-0 right-0 pr-3 flex items-center">
            <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-blue-600"></div>
          </div>
        )}
      </div>
      {searchText && (
        <p className="mt-2 text-sm text-gray-600">
          {isSearching ? 'Searching...' : 'Type to search for mutual funds'}
        </p>
      )}
    </div>
  );
};

export default FundSearch;
