import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { dashboardAPI } from '../../services/api.js';
import DashboardNavigation from './DashboardNavigation.jsx';
import FundSearch from './FundSearch.jsx';
import PortfolioSummary from './PortfolioSummary.jsx';
import UserFundDetail from './UserFundDetail.jsx';
import StockDataTable from './StockDataTable.jsx';
import SIPManagement from './SIPManagement.jsx';
import LoadingSpinner from '../common/LoadingSpinner.jsx';
import ErrorMessage from '../common/ErrorMessage.jsx';
import ThemeToggle from '../common/ThemeToggle.jsx';

const UserDashboard = ({ user, onLogout }) => {
  const [currentView, setCurrentView] = useState('dashboard');
  const [dashboardData, setDashboardData] = useState(null);
  const [dashboardExtraData, setDashboardExtraData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [loadingExtra, setLoadingExtra] = useState(false);
  const [error, setError] = useState(null);
  const [searchResults, setSearchResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [selectedFund, setSelectedFund] = useState(null);
  const [showExtraMetrics, setShowExtraMetrics] = useState(false);
  const [orderBy, setOrderBy] = useState('TOTAL_VALUE');
  const [orderDirection, setOrderDirection] = useState('DESC');

  const loadDashboardData = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await dashboardAPI.getUserDashboard(user.id);

      console.log('Dashboard API Response:', response);

      if (response) {
        setDashboardData(response);
      } else {
        setError('Failed to load dashboard data');
      }
    } catch (err) {
      console.error('Error loading dashboard:', err);
      setError('Failed to load dashboard data. Please try again.');
    } finally {
      setLoading(false);
    }
  }, [user.id]);

  const loadDashboardExtraData = useCallback(async () => {
    try {
      setLoadingExtra(true);
      setError(null);
      const response = await dashboardAPI.getUserDashboardExtra(user.id);

      console.log('Dashboard Extra API Response:', response);

      if (response) {
        setDashboardExtraData(response);
        setShowExtraMetrics(true);
      } else {
        setError('Failed to load extra dashboard data');
      }
    } catch (err) {
      console.error('Error loading dashboard extra:', err);
      setError('Failed to load extra dashboard data. Please try again.');
    } finally {
      setLoadingExtra(false);
    }
  }, [user.id]);

  useEffect(() => {
    if (user?.id) {
      loadDashboardData();
    }
  }, [user.id, loadDashboardData]);

  const handleSearch = async (searchText) => {
    if (!searchText.trim()) {
      setSearchResults([]);
      return;
    }

    try {
      setIsSearching(true);
      const response = await dashboardAPI.searchFunds(searchText);
      console.log('Search API Response:', response);

      if (response) {
        setSearchResults(response || []);
      } else {
        setSearchResults([]);
      }
    } catch (err) {
      console.error('Error searching funds:', err);
      setSearchResults([]);
    } finally {
      setIsSearching(false);
    }
  };

  const handleLogout = () => {
    if (window.confirm('Are you sure you want to logout?')) {
      onLogout();
    }
  };

  const handleFundSelect = (fund) => {
    // Convert fund data to MutualFundDTO format for UserFundDetail
    // Handle both search results (schemeCode/schemeName) and dashboard data (fundId/name)
    const fundForDetail = {
      schemeCode: fund.schemeCode || fund.fundId,
      schemeName: fund.schemeName || fund.name
    };
    setSelectedFund(fundForDetail);
  };

  const handleBackToDashboard = () => {
    setSelectedFund(null);
    // Refresh dashboard data when returning from UserFund page
    loadDashboardData();
  };

  // Memoized sorted fund summaries - only recalculates when dependencies change
  const sortedFundSummaries = useMemo(() => {
    if (!dashboardData?.fundSummaries) return [];

    return [...dashboardData.fundSummaries].sort((a, b) => {
      let comparison = 0;

      switch (orderBy) {
        case 'TOTAL_VALUE':
          comparison = (a.totalValue || 0) - (b.totalValue || 0);
          break;
        case 'PROFIT_PERCENT':
          comparison = (a.profitLossPercent || 0) - (b.profitLossPercent || 0);
          break;
        case 'TAG':
          const tagA = (a.tag || '').toLowerCase();
          const tagB = (b.tag || '').toLowerCase();
          comparison = tagA.localeCompare(tagB);
          break;
        case 'FUND_NAME':
          const nameA = (a.name || '').toLowerCase();
          const nameB = (b.name || '').toLowerCase();
          comparison = nameA.localeCompare(nameB);
          break;
        case 'TOTAL_INVESTED':
          comparison = (a.totalInvested || 0) - (b.totalInvested || 0);
          break;
        case 'PROFIT_LOSS':
          comparison = (a.profitLoss || 0) - (b.profitLoss || 0);
          break;
        default:
          comparison = 0;
      }

      return orderDirection === 'ASC' ? comparison : -comparison;
    });
  }, [dashboardData?.fundSummaries, orderBy, orderDirection]);

  const handleSortChange = (newOrderBy, newOrderDirection) => {
    setOrderBy(newOrderBy);
    setOrderDirection(newOrderDirection);
  };

  const handleRecordTransaction = (fund) => {
    // This will be handled by UserFundDetail component
    console.log('Record transaction for:', fund);
  };

  const handleRegisterSIP = (fund) => {
    // This will be handled by UserFundDetail component
    console.log('Register SIP for:', fund);
  };

  const renderCurrentView = () => {
    switch (currentView) {
      case 'sips':
        return <SIPManagement userId={user.id} onSuccess={(message) => console.log(message)} />;
      case 'transactions':
        return (
          <div className="text-center py-12">
            <div className="text-gray-500 dark:text-gray-400 text-lg mb-2">Transaction Management</div>
            <div className="text-gray-400 dark:text-gray-500">Transaction features coming soon...</div>
          </div>
        );
      case 'funds':
        return (
          <div className="text-center py-12">
            <div className="text-gray-500 dark:text-gray-400 text-lg mb-2">Fund Details</div>
            <div className="text-gray-400 dark:text-gray-500">Fund details features coming soon...</div>
          </div>
        );
      case 'profile':
        return (
          <div className="text-center py-12">
            <div className="text-gray-500 dark:text-gray-400 text-lg mb-2">User Profile</div>
            <div className="text-gray-400 dark:text-gray-500">Profile management coming soon...</div>
          </div>
        );
      case 'dashboard':
      default:
        return renderDashboard();
    }
  };

  const renderDashboard = () => (
    <>
      {/* Enhanced Search Section */}
      <div className="mb-8">
        <div className="bg-gradient-to-r from-slate-800 to-slate-900 rounded-xl p-6 text-white relative overflow-hidden mb-6">
          <div className="absolute inset-0 bg-gradient-to-r from-slate-700/30 to-slate-600/30"></div>
          <div className="relative z-10">
            <div className="flex items-center space-x-4 mb-4">
              <div className="p-3 bg-white/10 rounded-lg backdrop-blur-sm">
                <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
              </div>
              <div>
                <h2 className="text-2xl font-bold mb-2">Search Mutual Funds</h2>
                <p className="text-slate-300">Discover and analyze investment opportunities</p>
              </div>
            </div>
            <FundSearch onSearch={handleSearch} />
          </div>
        </div>
        {isSearching && (
          <div className="mt-6 flex items-center justify-center">
            <div className="bg-white rounded-xl p-6 shadow-md border border-gray-200">
              <div className="flex items-center space-x-4">
                <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-slate-600"></div>
                <div className="text-lg font-semibold text-gray-700">Searching mutual funds...</div>
              </div>
            </div>
          </div>
        )}
        {searchResults.length > 0 && (
          <div className="mt-6">
            <div className="bg-slate-50 rounded-lg p-6 border border-slate-200">
              <div className="flex justify-between items-center mb-6">
                <div className="flex items-center space-x-3">
                  <div className="p-2 bg-slate-600 rounded-lg">
                    <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                    </svg>
                  </div>
                  <div>
                    <h3 className="text-xl font-bold text-gray-900">Search Results</h3>
                    <p className="text-sm text-gray-600">Found {searchResults.length} mutual funds</p>
                  </div>
                </div>
                <button
                  onClick={() => setSearchResults([])}
                  className="p-2 bg-white rounded-lg shadow-sm hover:shadow-md transition-all duration-200 hover:scale-105"
                  title="Close search results"
                >
                  <svg className="h-5 w-5 text-gray-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {searchResults.map((fund, index) => (
                  <div 
                    key={fund.schemeCode} 
                    className="bg-white p-6 rounded-lg shadow-md border border-gray-200 hover:shadow-lg hover:scale-105 cursor-pointer transition-all duration-300 group"
                    onClick={() => handleFundSelect(fund)}
                    style={{ animationDelay: `${index * 50}ms` }}
                  >
                    <div>
                      <div className="flex items-start justify-between mb-4">
                        <div className="p-3 bg-slate-600 rounded-lg group-hover:scale-110 transition-transform duration-300">
                          <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                          </svg>
                        </div>
                        <div className="text-right">
                          <div className="text-xs font-semibold text-blue-600 bg-blue-50 px-2 py-1 rounded-full">
                            {fund.schemeCode}
                          </div>
                        </div>
                      </div>
                      <h4 className="text-lg font-bold text-gray-900 group-hover:text-blue-600 transition-colors duration-300 mb-2">
                        {fund.schemeName}
                      </h4>
                      <p className="text-sm text-gray-600 mb-4">Click to view detailed information and analysis</p>
                      <div className="flex items-center text-blue-600 font-semibold text-sm">
                        <span>View Details</span>
                        <svg className="w-4 h-4 ml-2 group-hover:translate-x-1 transition-transform duration-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                        </svg>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Dashboard Content */}
      {dashboardData ? (
        <div className="space-y-8">
          {/* Portfolio Summary */}
          <PortfolioSummary 
            dashboardData={dashboardData} 
            dashboardExtraData={dashboardExtraData}
            onLoadExtraData={loadDashboardExtraData}
            loadingExtra={loadingExtra}
            showExtraMetrics={showExtraMetrics}
          />

          {/* Fund Data Table */}
          {dashboardData.fundSummaries && dashboardData.fundSummaries.length > 0 && (
            <div>
              <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between mb-6 space-y-4 lg:space-y-0">
                <div>
                  <h2 className="text-2xl font-bold text-gray-900 dark:text-white">Your Investments</h2>
                  <p className="text-sm text-gray-500 dark:text-gray-400 mt-1">Click on any fund to view detailed information</p>
                </div>

              {/* Enhanced Filter Options */}
              <div className="bg-white rounded-lg shadow-sm border border-gray-100 p-4">
                <div className="flex items-center gap-2">
                  {[
                    { value: 'TOTAL_VALUE', label: 'Total Value', icon: 'Wallet', color: 'from-blue-600 to-blue-700' },
                    { value: 'PROFIT_PERCENT', label: 'Profit %', icon: 'TrendingUp', color: 'from-green-600 to-green-700' },
                    { value: 'TAG', label: 'Tag', icon: 'Tag', color: 'from-purple-600 to-purple-700' },
                    { value: 'FUND_NAME', label: 'Fund Name', icon: 'BarChart3', color: 'from-indigo-600 to-indigo-700' }
                  ].map((option) => {
                    const isActive = orderBy === option.value;
                    const IconComponent = {
                      Wallet: () => <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" /></svg>,
                      TrendingUp: () => <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6" /></svg>,
                      Tag: () => <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 7h.01M7 3h5c.512 0 1.024.195 1.414.586l7 7a2 2 0 010 2.828l-7 7a2 2 0 01-2.828 0l-7-7A1.994 1.994 0 013 12V7a4 4 0 014-4z" /></svg>,
                      BarChart3: () => <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" /></svg>
                    }[option.icon];

                    return (
                      <button
                        key={option.value}
                        onClick={() => handleSortChange(option.value, orderBy === option.value && orderDirection === 'ASC' ? 'DESC' : 'ASC')}
                        className={`px-3 py-2 text-xs font-medium rounded-lg transition-all ${
                          isActive
                            ? `bg-gray-900 text-white`
                            : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                        }`}
                      >
                        <div className="flex items-center space-x-2">
                          {IconComponent && <IconComponent />}
                          <span>{option.label}</span>
                          {isActive && (
                            <div className="flex items-center space-x-1">
                              {orderDirection === 'ASC' ? (
                                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 15l7-7 7 7" />
                                </svg>
                              ) : (
                                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                                </svg>
                              )}
                            </div>
                          )}
                        </div>
                      </button>
                    );
                  })}
                </div>
              </div>
            </div>

            <StockDataTable 
              fundSummaries={sortedFundSummaries} 
              onFundClick={handleFundSelect}
            />
          </div>
          )}

          {/* Empty State */}
          {(!dashboardData.fundSummaries || dashboardData.fundSummaries.length === 0) && (
            <div className="text-center py-12">
              <div className="text-gray-500 dark:text-gray-400 text-lg mb-2">No investments found</div>
              <div className="text-gray-400 dark:text-gray-500">Start by searching for mutual funds above</div>
            </div>
          )}
        </div>
      ) : (
        <div className="text-center py-12">
          <div className="text-gray-500 dark:text-gray-400 text-lg mb-2">Welcome to your dashboard!</div>
          <div className="text-gray-400 dark:text-gray-500">Search for mutual funds to get started</div>
        </div>
      )}
    </>
  );

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <LoadingSpinner />
      </div>
    );
  }

  // Show UserFundDetail if a fund is selected
  if (selectedFund) {
    return (
      <UserFundDetail
        fund={selectedFund}
        userId={user.id}
        onBack={handleBackToDashboard}
        onRecordTransaction={handleRecordTransaction}
        onRegisterSIP={handleRegisterSIP}
      />
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      {/* Navigation */}
      <div className="relative z-10">
        <DashboardNavigation 
          currentView={currentView}
          onViewChange={setCurrentView}
          onLogout={handleLogout}
          user={user}
        />
      </div>

      {/* Main Content */}
      <main className="relative z-10 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {error && (
          <div className="mb-6">
            <ErrorMessage message={error} onClose={() => setError(null)} />
          </div>
        )}

        {renderCurrentView()}
      </main>
    </div>
  );
};

export default UserDashboard;
