import React from 'react';
import ThemeToggle from '../common/ThemeToggle.jsx';

const DashboardNavigation = ({ currentView, onViewChange, onLogout, user }) => {
  return (
    <nav className="bg-white/90 dark:bg-gray-800/90 backdrop-blur supports-backdrop-blur:bg-white/60 dark:supports-backdrop-blur:bg-gray-800/60 shadow-sm border-b border-gray-200 dark:border-gray-700">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          {/* Brand */}
          <div className="flex items-center">
            <div className="flex-shrink-0 select-none">
              <span className="text-3xl sm:text-4xl font-extrabold tracking-tight bg-gradient-to-r from-blue-600 via-purple-600 to-indigo-600 bg-clip-text text-transparent drop-shadow-sm">
                Stonks
              </span>
            </div>
            {/* Small button group to the right of brand */}
            <div className="ml-4 hidden sm:flex items-center space-x-2">
              <button
                onClick={() => onViewChange && onViewChange('dashboard')}
                className={`px-3 py-1.5 rounded-md text-xs font-medium border transition-colors shadow-sm ${
                  currentView === 'dashboard' ? 'bg-blue-100 dark:bg-blue-900 text-blue-700 dark:text-blue-300 border-blue-200 dark:border-blue-700' : 'bg-white/70 dark:bg-gray-700/70 text-gray-700 dark:text-gray-300 border-transparent hover:bg-gray-50 dark:hover:bg-gray-600'
                }`}
              >
                Dashboard
              </button>
              <button
                onClick={() => onViewChange && onViewChange('sips')}
                className={`px-3 py-1.5 rounded-md text-xs font-medium border transition-colors shadow-sm ${
                  currentView === 'sips' ? 'bg-purple-100 dark:bg-purple-900 text-purple-700 dark:text-purple-300 border-purple-200 dark:border-purple-700' : 'bg-white/70 dark:bg-gray-700/70 text-gray-700 dark:text-gray-300 border-transparent hover:bg-gray-50 dark:hover:bg-gray-600'
                }`}
              >
                SIPs
              </button>
              <button
                onClick={() => onViewChange && onViewChange('profile')}
                className={`px-3 py-1.5 rounded-md text-xs font-medium border transition-colors shadow-sm ${
                  currentView === 'profile' ? 'bg-gray-200 dark:bg-gray-600 text-gray-900 dark:text-gray-100 border-gray-300 dark:border-gray-500' : 'bg-white/70 dark:bg-gray-700/70 text-gray-700 dark:text-gray-300 border-transparent hover:bg-gray-50 dark:hover:bg-gray-600'
                }`}
              >
                Profile
              </button>
            </div>
          </div>

          {/* Right actions: Theme Toggle, Welcome, Logout */}
          <div className="flex items-center space-x-3">
            <ThemeToggle />
            <div className="hidden sm:flex flex-col items-start">
              <div className="text-base sm:text-lg text-gray-700 dark:text-gray-300">
                Welcome, <span className="font-medium">{user.name}</span>
              </div>
            </div>
            <button
              onClick={onLogout}
              className="px-4 py-2 rounded-md text-sm font-medium text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2 dark:focus:ring-offset-gray-800"
            >
              Logout
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default DashboardNavigation;
