import React, { useState, useEffect } from 'react';
// import { UserDashboardDTO, FundSummaryDTO, MutualFundDTO } from '../../types/index.js';
import { dashboardAPI } from '../../services/api.js';
import FundSearch from './FundSearch.jsx';
import FundSummaryCard from './FundSummaryCard.jsx';
import PortfolioSummary from './PortfolioSummary.jsx';
import LoadingSpinner from '../common/LoadingSpinner.jsx';
import ErrorMessage from '../common/ErrorMessage.jsx';

const Dashboard = () => {
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchResults, setSearchResults] = useState([]);
  const [showSearchResults, setShowSearchResults] = useState(false);

  // For demo purposes, using a hardcoded user ID
  // In a real app, this would come from authentication context
  const userId = 'demo-user-123';

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await dashboardAPI.getUserDashboard(userId);
      setDashboardData(data);
    } catch (err) {
      setError('Failed to load dashboard data. Please try again.');
      console.error('Dashboard fetch error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleFundSearch = async (searchText) => {
    if (!searchText.trim()) {
      setSearchResults([]);
      setShowSearchResults(false);
      return;
    }

    try {
      const results = await dashboardAPI.searchFunds(searchText);
      setSearchResults(results);
      setShowSearchResults(true);
    } catch (err) {
      console.error('Fund search error:', err);
      setError('Failed to search funds. Please try again.');
    }
  };

  const handleSearchClose = () => {
    setShowSearchResults(false);
    setSearchResults([]);
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  if (error) {
    return <ErrorMessage message={error} onRetry={fetchDashboardData} />;
  }

  if (!dashboardData) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">No Dashboard Data</h2>
          <p className="text-gray-600 mb-4">Unable to load your dashboard data.</p>
          <button
            onClick={fetchDashboardData}
            className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">Stonks</h1>
              <p className="text-gray-600">Welcome back, {dashboardData.userName}!</p>
            </div>
            <div className="flex items-center space-x-4">
              <button
                onClick={fetchDashboardData}
                className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
              >
                Refresh
              </button>
            </div>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Fund Search Section */}
        <div className="mb-8">
          <FundSearch onSearch={handleFundSearch} />
          {showSearchResults && (
            <div className="mt-4">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-semibold text-gray-900">
                  Search Results ({searchResults.length})
                </h3>
                <button
                  onClick={handleSearchClose}
                  className="text-gray-500 hover:text-gray-700"
                >
                  âœ•
                </button>
              </div>
              <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
                {searchResults.map((fund) => (
                  <div
                    key={fund.schemeCode}
                    className="bg-white p-4 rounded-lg shadow-sm border hover:shadow-md transition-shadow"
                  >
                    <h4 className="font-medium text-gray-900">{fund.schemeName}</h4>
                    <p className="text-sm text-gray-600">Code: {fund.schemeCode}</p>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Portfolio Summary */}
        <PortfolioSummary dashboardData={dashboardData} />

        {/* Fund Summaries */}
        <div className="mt-8">
          <h2 className="text-2xl font-bold text-gray-900 mb-6">Your Funds</h2>
          {dashboardData.fundSummaries.length === 0 ? (
            <div className="text-center py-12">
              <div className="text-gray-400 mb-4">
                <svg className="mx-auto h-12 w-12" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                </svg>
              </div>
              <h3 className="text-lg font-medium text-gray-900 mb-2">No funds yet</h3>
              <p className="text-gray-600">Start by searching for funds above to add them to your portfolio.</p>
            </div>
          ) : (
            <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
              {dashboardData.fundSummaries.map((fund) => (
                <FundSummaryCard key={fund.fundId} fund={fund} />
              ))}
            </div>
          )}
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
