import React from 'react';

const StockDataTable = ({ fundSummaries, onFundClick }) => {
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 2,
    }).format(amount || 0);
  };

  const formatPercentage = (value) => {
    if (value == null || isNaN(value)) {
      return 'N/A';
    }
    return `${value.toFixed(2)}%`;
  };

  const getProfitLossColor = (value) => {
    if (value > 0) return 'text-green-600';
    if (value < 0) return 'text-red-600';
    return 'text-gray-600';
  };

  const getProfitLossBgColor = (value) => {
    if (value > 0) return 'bg-green-50';
    if (value < 0) return 'bg-red-50';
    return 'bg-gray-50';
  };

  if (!fundSummaries || fundSummaries.length === 0) {
    return (
      <div className="text-center py-12">
        <div className="text-gray-500 text-lg mb-2">No investments found</div>
        <div className="text-gray-400">Start by searching for mutual funds above</div>
      </div>
    );
  }

  return (
    <div className="bg-white shadow-sm rounded-lg overflow-hidden">
      <div className="px-6 py-4 border-b border-gray-200">
        <h3 className="text-lg font-medium text-gray-900">Your Investments</h3>
        <p className="text-sm text-gray-500">Click on any fund to view detailed information</p>
      </div>

      <div className="overflow-hidden">
        <table className="w-full divide-y divide-gray-200 table-fixed">
          <thead className="bg-gray-50">
            <tr>
              <th className="w-2/5 px-6 py-4 text-left text-sm font-semibold text-gray-700 uppercase tracking-wider">
                Fund Name
              </th>
              <th className="w-1/6 px-6 py-4 text-left text-sm font-semibold text-gray-700 uppercase tracking-wider">
                Tag
              </th>
              <th className="w-1/6 px-6 py-4 text-right text-sm font-semibold text-gray-700 uppercase tracking-wider">
                Invested
              </th>
              <th className="w-1/6 px-6 py-4 text-right text-sm font-semibold text-gray-700 uppercase tracking-wider">
                Current Value
              </th>
              <th className="w-1/6 px-6 py-4 text-right text-sm font-semibold text-gray-700 uppercase tracking-wider">
                P&L
              </th>
              <th className="w-1/5 px-6 py-4 text-right text-sm font-semibold text-gray-700 uppercase tracking-wider">
                P&L %
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {fundSummaries.map((fund) => (
              <tr
                key={fund.fundId || fund.schemeCode}
                className="hover:bg-gray-50 cursor-pointer transition-colors duration-150"
                onClick={() => onFundClick(fund)}
              >
                <td className="px-6 py-5">
                  <div className="flex items-start">
                    <div className="flex-shrink-0 h-10 w-10">
                      <div className="h-10 w-10 rounded-full bg-blue-100 flex items-center justify-center">
                        <span className="text-blue-600 font-medium text-sm">
                          {(fund.name || fund.schemeName).charAt(0).toUpperCase()}
                        </span>
                      </div>
                    </div>
                    <div className="ml-4 min-w-0 flex-1">
                      <div className="text-base font-semibold text-gray-900 break-words">
                        {fund.name || fund.schemeName}
                      </div>
                      <div className="mt-1">
                        {fund.isEmergency && (
                          <span className="inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
                            Emergency
                          </span>
                        )}
                      </div>
                    </div>
                  </div>
                </td>
                <td className="px-6 py-5">
                  <div className="flex items-center">
                    {fund.tag && (
                      <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-semibold bg-blue-100 text-blue-800">
                        {fund.tag}
                      </span>
                    )}
                    {!fund.tag && (
                      <span className="text-gray-400 text-base">-</span>
                    )}
                  </div>
                </td>
                <td className="px-6 py-5 text-right text-base font-semibold text-gray-900">
                  {formatCurrency(fund.totalInvested)}
                </td>
                <td className="px-6 py-5 text-right text-base font-semibold text-gray-900">
                  {formatCurrency(fund.totalValue)}
                </td>
                <td className="px-6 py-5 text-right">
                  <span className={`text-base font-semibold ${getProfitLossColor(fund.profitLoss)}`}>
                    {formatCurrency(fund.profitLoss)}
                  </span>
                </td>
                <td className="px-6 py-5 text-right">
                  <span className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-semibold ${getProfitLossBgColor(fund.profitLossPercent)} ${getProfitLossColor(fund.profitLossPercent)}`}>
                    {formatPercentage(fund.profitLossPercent)}
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="px-6 py-3 bg-gray-50 border-t border-gray-200">
        <div className="text-sm text-gray-500">
          Click on any row to view detailed fund information, transactions, and SIP details
        </div>
      </div>
    </div>
  );
};

export default StockDataTable;
