import React from 'react';

const MessagePopup = ({ 
  isOpen, 
  onClose, 
  type = 'success', // 'success' or 'error'
  title, 
  message, 
  autoClose = true,
  autoCloseDelay = 3000 
}) => {
  React.useEffect(() => {
    if (isOpen && autoClose) {
      const timer = setTimeout(() => {
        onClose();
      }, autoCloseDelay);
      return () => clearTimeout(timer);
    }
  }, [isOpen, autoClose, autoCloseDelay, onClose]);

  if (!isOpen) return null;

  const isSuccess = type === 'success';
  const bgColor = isSuccess ? 'from-green-50 to-emerald-50' : 'from-red-50 to-pink-50';
  const borderColor = isSuccess ? 'border-green-200' : 'border-red-200';
  const iconBgColor = isSuccess ? 'bg-green-500' : 'bg-red-500';
  const textColor = isSuccess ? 'text-green-800' : 'text-red-800';
  const buttonColor = isSuccess ? 'bg-green-600 hover:bg-green-700' : 'bg-red-600 hover:bg-red-700';

  return (
    <div className="fixed inset-0 z-50 overflow-y-auto">
      {/* Backdrop */}
      <div className="fixed inset-0 bg-black bg-opacity-50 transition-opacity" onClick={onClose}></div>

      {/* Modal */}
      <div className="flex min-h-full items-center justify-center p-4">
        <div className={`relative transform overflow-hidden rounded-2xl bg-gradient-to-r ${bgColor} border ${borderColor} shadow-2xl transition-all`}>
          {/* Close button */}
          <button
            onClick={onClose}
            className="absolute right-4 top-4 text-gray-400 hover:text-gray-600 focus:outline-none"
          >
            <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>

          {/* Content */}
          <div className="p-8">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <div className={`w-16 h-16 ${iconBgColor} rounded-full flex items-center justify-center`}>
                  {isSuccess ? (
                    <svg className="h-8 w-8 text-white" viewBox="0 0 20 20" fill="currentColor">
                      <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                    </svg>
                  ) : (
                    <svg className="h-8 w-8 text-white" viewBox="0 0 20 20" fill="currentColor">
                      <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                    </svg>
                  )}
                </div>
              </div>
              <div className="ml-6 flex-1">
                <h3 className={`text-lg font-semibold ${textColor} mb-2`}>
                  {title || (isSuccess ? 'Success!' : 'Error!')}
                </h3>
                <p className={`text-sm ${textColor} mb-6`}>
                  {message}
                </p>
                <div className="flex justify-end">
                  <button
                    onClick={onClose}
                    className={`px-6 py-2 text-white rounded-lg transition-colors duration-200 ${buttonColor}`}
                  >
                    {isSuccess ? 'Great!' : 'OK'}
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MessagePopup;
