import React from 'react';
import { 
  TrendingUp, 
  TrendingDown, 
  DollarSign, 
  Target, 
  Shield, 
  PieChart, 
  Calendar,
  Award,
  BarChart3,
  Activity,
  Zap
} from 'lucide-react';

const PortfolioSummary = ({ dashboardData }) => {
  const isProfit = dashboardData.profitLoss >= 0;
  const profitLossColor = isProfit ? 'text-green-600' : 'text-red-600';
  const profitLossBgColor = isProfit ? 'bg-green-50' : 'bg-red-50';

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount || 0);
  };

  const formatPercentage = (percentage) => {
    if (percentage == null || isNaN(percentage)) {
      return 'N/A';
    }
    return `${percentage >= 0 ? '+' : ''}${percentage.toFixed(2)}%`;
  };

  const formatXIRR = (xirr) => {
    if (xirr == null || isNaN(xirr)) {
      return 'N/A';
    }
    return `${xirr.toFixed(2)}%`;
  };

  // Calculate additional metrics
  const totalFunds = dashboardData.fundSummaries?.length || 0;
  const emergencyFunds = dashboardData.fundSummaries?.filter(fund => fund.isEmergency) || [];
  const emergencyFundCount = emergencyFunds.length;
  const totalRealizedProfit = dashboardData.fundSummaries?.reduce((sum, fund) => sum + (fund.totalRealizedProfit || 0), 0) || 0;
  const currentYearRealizedProfit = dashboardData.fundSummaries?.reduce((sum, fund) => sum + (fund.currentYearTotalRealizedProfit || 0), 0) || 0;

  const mainMetrics = [
    {
      title: 'Total Invested',
      value: formatCurrency(dashboardData.totalInvested),
      icon: DollarSign,
      color: 'text-blue-600',
      bgColor: 'bg-gradient-to-br from-blue-50 to-blue-100',
      iconBg: 'bg-blue-500',
      description: 'Total amount invested across all funds'
    },
    {
      title: 'Current Value',
      value: formatCurrency(dashboardData.totalValue),
      icon: Target,
      color: 'text-purple-600',
      bgColor: 'bg-gradient-to-br from-purple-50 to-purple-100',
      iconBg: 'bg-purple-500',
      description: 'Current market value of your portfolio'
    },
    {
      title: 'Unrealized P&L',
      value: formatCurrency(Math.abs(dashboardData.profitLoss)),
      percentage: formatPercentage(dashboardData.profitLossPercent),
      icon: isProfit ? TrendingUp : TrendingDown,
      color: profitLossColor,
      bgColor: isProfit ? 'bg-gradient-to-br from-green-50 to-green-100' : 'bg-gradient-to-br from-red-50 to-red-100',
      iconBg: isProfit ? 'bg-green-500' : 'bg-red-500',
      description: 'Current unrealized profit/loss'
    },
    {
      title: 'XIRR',
      value: formatXIRR(dashboardData.xirr),
      icon: Activity,
      color: 'text-indigo-600',
      bgColor: 'bg-gradient-to-br from-indigo-50 to-indigo-100',
      iconBg: 'bg-indigo-500',
      description: 'Extended Internal Rate of Return'
    }
  ];

  const additionalMetrics = [
    {
      title: 'Realized Profit',
      value: formatCurrency(totalRealizedProfit),
      icon: Award,
      color: 'text-emerald-600',
      bgColor: 'bg-gradient-to-br from-emerald-50 to-emerald-100',
      iconBg: 'bg-emerald-500',
      description: 'Total profit from sold investments'
    },
    {
      title: 'Current Year Realized',
      value: formatCurrency(currentYearRealizedProfit),
      icon: Calendar,
      color: 'text-orange-600',
