import React, { useState, useEffect } from 'react';
import { X } from 'lucide-react';
import { ComposedChart, Line, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, Brush, Cell } from 'recharts';
import LoadingSpinner from '../common/LoadingSpinner.jsx';

const HistoricDataModal = ({ userId, fundId, title, onClose, fetchData }) => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);
        setError(null);
        const response = await fetchData(userId, fundId);
        setData(response || []);
      } catch (err) {
        console.error('Error loading historic data:', err);
        setError('Failed to load historic data');
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, [userId, fundId, fetchData]);

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount || 0);
  };

  const formatPercentage = (value) => {
    return `${value?.toFixed(2) || 0}%`;
  };

  if (loading) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-white rounded-xl shadow-2xl max-w-7xl w-full max-h-[90vh] flex items-center justify-center p-8">
          <LoadingSpinner />
        </div>
      </div>
    );
  }

  if (error || !data || data.length === 0) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-white rounded-xl shadow-2xl max-w-7xl w-full max-h-[90vh] p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-2xl font-bold text-gray-900">{title}</h2>
            <button
              onClick={onClose}
              className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-all duration-200"
            >
              <X className="w-6 h-6" />
            </button>
          </div>
          <div className="text-center py-12">
            <p className="text-gray-600">{error || 'No historic data available'}</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-2xl max-w-7xl w-full max-h-[90vh] flex flex-col">
        {/* Header */}
        <div className="flex items-center justify-between px-6 py-4 border-b border-gray-200">
          <h2 className="text-2xl font-bold text-gray-900">{title}</h2>
          <button
            onClick={onClose}
            className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-all duration-200"
          >
            <X className="w-6 h-6" />
          </button>
        </div>

        {/* Content */}
        <div className="flex-1 overflow-y-auto p-6">
          <div className="space-y-8">
            {/* Graph 1: Total Value, Total Invested, This Month Invested */}
            <div className="bg-white rounded-lg border border-gray-200 p-6">
              <h3 className="text-lg font-bold text-gray-900 mb-4">
                Investment Overview
              </h3>
              <ResponsiveContainer width="100%" height={500}>
                <ComposedChart data={data} margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="month" />
                  <YAxis tickFormatter={formatCurrency} />
                  <Tooltip formatter={(value) => formatCurrency(value)} />
                  <Legend />
                  <Line type="monotone" dataKey="totalValue" stroke="#8884d8" strokeWidth={2} dot={{ fill: '#8884d8' }} activeDot={{ r: 6 }} name="Total Value" />
                  <Line type="monotone" dataKey="totalInvested" stroke="#82ca9d" strokeWidth={2} dot={{ fill: '#82ca9d' }} activeDot={{ r: 6 }} name="Total Investment" />
                  <Bar dataKey="thisMonthInvested" fill="#ffc658" name="Month's Investment" />
                  <Brush dataKey="month" height={30} stroke="#8884d8" />
                </ComposedChart>
              </ResponsiveContainer>
            </div>

            {/* Graph 2: This Month Profit, Total Profit, Growth % */}
            <div className="bg-white rounded-lg border border-gray-200 p-6">
              <h3 className="text-lg font-bold text-gray-900 mb-4">
                Profit & Growth Analysis
              </h3>
              <ResponsiveContainer width="100%" height={500}>
                <ComposedChart data={data} margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="month" />
                  <YAxis yAxisId="left" tickFormatter={formatCurrency} />
                  <YAxis yAxisId="right" orientation="right" tickFormatter={formatPercentage} />
                  <Tooltip 
                    content={({ active, payload }) => {
                      if (!active || !payload || !payload.length) return null;

                      return (
                        <div className="bg-white border border-gray-300 rounded-lg shadow-lg p-3">
                          {payload.map((entry, index) => {
                            let value = entry.value;
                            let formattedValue;

                            if (entry.dataKey === 'growthPercent') {
                              formattedValue = formatPercentage(value);
                            } else {
                              formattedValue = formatCurrency(value);
                            }

                            return (
                              <p key={index} style={{ color: entry.color }}>
                                {`${entry.name}: ${formattedValue}`}
                              </p>
                            );
                          })}
                        </div>
                      );
                    }}
                  />
                  <Legend />
                  <Bar yAxisId="left" dataKey="thisMonthProfit" name="Month's Profit">
                    {data.map((entry, index) => {
                      const color = entry.thisMonthProfit >= 0 ? '#10b981' : '#ef4444';
                      return <Cell key={`cell-${index}`} fill={color} />;
                    })}
                  </Bar>
                  <Line yAxisId="left" type="monotone" dataKey="totalProfit" stroke="#82ca9d" strokeWidth={2} dot={{ fill: '#82ca9d' }} activeDot={{ r: 6 }} name="Total Profit" />
                  <Line yAxisId="right" type="monotone" dataKey="growthPercent" stroke="#ffc658" strokeWidth={2} strokeDasharray="5 5" dot={{ fill: '#ffc658' }} activeDot={{ r: 6 }} name="Growth %" />
                  <Brush dataKey="month" height={30} stroke="#8884d8" />
                </ComposedChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HistoricDataModal;
