import React from 'react';
import { UserDashboardDTO } from '../../types/index.js';
import { TrendingUp, TrendingDown, DollarSign, Target, Shield } from 'lucide-react';

interface PortfolioSummaryProps {
  dashboardData: UserDashboardDTO;
}

const PortfolioSummary: React.FC<PortfolioSummaryProps> = ({ dashboardData }) => {
  const isProfit = dashboardData.profitLoss >= 0;
  const profitLossColor = isProfit ? 'text-green-600' : 'text-red-600';
  const profitLossBgColor = isProfit ? 'bg-green-50' : 'bg-red-50';

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatPercentage = (percentage: number) => {
    return `${percentage >= 0 ? '+' : ''}${percentage.toFixed(2)}%`;
  };

  const summaryCards = [
    {
      title: 'Total Invested',
      value: formatCurrency(dashboardData.totalInvested),
      icon: DollarSign,
      color: 'text-blue-600',
      bgColor: 'bg-blue-50',
    },
    {
      title: 'Current Value',
      value: formatCurrency(dashboardData.totalValue),
      icon: Target,
      color: 'text-purple-600',
      bgColor: 'bg-purple-50',
    },
    {
      title: 'Profit/Loss',
      value: formatCurrency(Math.abs(dashboardData.profitLoss)),
      percentage: formatPercentage(dashboardData.profitLossPercent),
      icon: isProfit ? TrendingUp : TrendingDown,
      color: profitLossColor,
      bgColor: profitLossBgColor,
    },
    {
      title: 'XIRR',
      value: `${dashboardData.xirr.toFixed(2)}%`,
      icon: TrendingUp,
      color: 'text-indigo-600',
      bgColor: 'bg-indigo-50',
    },
  ];

  return (
    <div className="bg-white rounded-lg shadow-sm border p-6">
      <h2 className="text-2xl font-bold text-gray-900 mb-6">Portfolio Overview</h2>

      {/* Summary Cards Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-6">
        {summaryCards.map((card, index) => (
          <div key={index} className={`p-4 rounded-lg ${card.bgColor}`}>
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600 mb-1">{card.title}</p>
                <p className={`text-2xl font-bold ${card.color}`}>{card.value}</p>
                {card.percentage && (
                  <p className={`text-sm font-medium ${card.color} mt-1`}>
                    {card.percentage}
                  </p>
                )}
              </div>
              <div className={`p-3 rounded-full ${card.bgColor}`}>
                <card.icon className={`w-6 h-6 ${card.color}`} />
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Emergency Fund */}
      {dashboardData.totalEmergencyFundValue > 0 && (
        <div className="border-t border-gray-200 pt-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <Shield className="w-5 h-5 text-yellow-600 mr-2" />
              <span className="text-sm font-medium text-gray-700">Emergency Fund Value</span>
            </div>
            <span className="text-lg font-semibold text-gray-900">
              {formatCurrency(dashboardData.totalEmergencyFundValue)}
            </span>
          </div>
        </div>
      )}

      {/* Fund Count */}
      <div className="border-t border-gray-200 pt-4 mt-4">
        <div className="flex items-center justify-between text-sm text-gray-600">
          <span>Total Funds</span>
          <span className="font-medium">{dashboardData.fundSummaries.length}</span>
        </div>
      </div>
    </div>
  );
};

export default PortfolioSummary;
