import React, { useState, useEffect } from 'react';
import { userAPI } from '../../services/api.js';
import { LogIn, TrendingUp, BarChart3, Shield, Zap, ArrowRight, Eye, EyeOff } from 'lucide-react';

const Login = ({ onLogin, onSwitchToSignup }) => {
  const [formData, setFormData] = useState({
    loginId: '',
    password: ''
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  useEffect(() => {
    // Check if user was redirected from signup
    const urlParams = new URLSearchParams(window.location.search);
    const fromSignup = urlParams.get('fromSignup');
    if (fromSignup === 'true') {
      setSuccessMessage('Account created successfully! Please login with your credentials.');
      // Clean up URL
      window.history.replaceState({}, document.title, window.location.pathname);
    }
  }, []);

  const validateForm = () => {
    const newErrors = {};

    if (!formData.loginId.trim()) {
      newErrors.loginId = 'Login ID is required';
    }

    if (!formData.password.trim()) {
      newErrors.password = 'Password is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    try {
      setIsLoading(true);
      setErrors({});

      const response = await userAPI.login(formData);

      if (response) {
        onLogin(response);
      } else {
        setErrors({ general: 'Invalid login credentials' });
      }
    } catch (error) {
      console.error('Login error:', error);
      setErrors({ general: 'Login failed. Please try again.' });
    } finally {
      setIsLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }

    // Clear success message when user starts typing
    if (successMessage) {
      setSuccessMessage('');
    }
  };

  const features = [
    { icon: BarChart3, text: 'Track Performance', color: 'from-blue-500 to-blue-600' },
    { icon: TrendingUp, text: 'Portfolio Analytics', color: 'from-purple-500 to-purple-600' },
    { icon: Shield, text: 'Secure & Private', color: 'from-indigo-500 to-indigo-600' },
    { icon: Zap, text: 'Real-time Updates', color: 'from-pink-500 to-pink-600' }
  ];

  return (
    <div className="min-h-screen flex bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      {/* Left Side - Branding & Features */}
      <div className="hidden lg:flex lg:w-1/2 flex-col justify-center px-12 bg-gradient-to-br from-blue-600 via-purple-600 to-indigo-700 relative overflow-hidden">
        {/* Background Pattern */}
        <div className="absolute inset-0 opacity-10">
          <div className="absolute top-0 left-0 w-96 h-96 bg-white rounded-full blur-3xl"></div>
          <div className="absolute bottom-0 right-0 w-96 h-96 bg-white rounded-full blur-3xl"></div>
        </div>

        <div className="relative z-10">
          {/* Logo & Brand */}
          <div className="mb-12">
            <div className="flex items-center space-x-4 mb-6">
              <div className="w-16 h-16 bg-white/20 backdrop-blur-lg rounded-2xl flex items-center justify-center shadow-2xl">
                <img src="/Stonks.jpeg" alt="Stonks" className="w-12 h-12 rounded-xl" />
              </div>
              <div>
                <h1 className="text-5xl font-black text-white mb-2 tracking-tight">
                  Stonks
                </h1>
                <p className="text-blue-100 text-sm font-semibold uppercase tracking-wider">
                  Investment Tracking Platform
                </p>
              </div>
            </div>
            <p className="text-blue-100 text-lg leading-relaxed max-w-md">
              Your comprehensive solution for tracking mutual fund investments, analyzing performance, and making informed financial decisions.
            </p>
          </div>

          {/* Features */}
          <div className="space-y-4">
            {features.map((feature, index) => {
              const Icon = feature.icon;
              return (
                <div key={index} className="flex items-center space-x-4 group">
                  <div className={`w-12 h-12 bg-gradient-to-r ${feature.color} rounded-xl flex items-center justify-center shadow-lg group-hover:scale-110 transition-transform duration-300`}>
                    <Icon className="w-6 h-6 text-white" />
                  </div>
                  <span className="text-white text-lg font-semibold">{feature.text}</span>
                </div>
              );
            })}
          </div>

          {/* Stats or Additional Info */}
          <div className="mt-12 pt-8 border-t border-white/20">
            <div className="grid grid-cols-2 gap-6">
              <div>
                <div className="text-3xl font-bold text-white">100%</div>
                <div className="text-blue-100 text-sm mt-1">Secure</div>
              </div>
              <div>
                <div className="text-3xl font-bold text-white">24/7</div>
                <div className="text-blue-100 text-sm mt-1">Access</div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Right Side - Login Form */}
      <div className="w-full lg:w-1/2 flex items-center justify-center p-8">
        <div className="w-full max-w-md">
          {/* Mobile Logo */}
          <div className="lg:hidden mb-8 text-center">
            <div className="flex items-center justify-center space-x-3 mb-4">
              <div className="w-12 h-12 bg-gradient-to-r from-blue-600 to-purple-600 rounded-xl flex items-center justify-center shadow-lg">
                <img src="/Stonks.jpeg" alt="Stonks" className="w-10 h-10 rounded-lg" />
              </div>
              <div>
                <h1 className="text-3xl font-black bg-gradient-to-r from-blue-600 via-purple-600 to-indigo-600 bg-clip-text text-transparent">
                  Stonks
                </h1>
                <p className="text-gray-500 text-xs font-semibold uppercase tracking-wider">
                  Investment Tracking
                </p>
              </div>
            </div>
          </div>

          {/* Form Card */}
          <div className="bg-white rounded-2xl shadow-2xl p-8 border border-gray-100">
            <div className="mb-8">
              <div className="flex items-center space-x-3 mb-2">
                <div className="w-10 h-10 bg-gradient-to-r from-blue-600 to-purple-600 rounded-lg flex items-center justify-center">
                  <LogIn className="w-5 h-5 text-white" />
                </div>
                <h2 className="text-3xl font-bold text-gray-900">
                  Welcome Back
                </h2>
              </div>
              <p className="text-gray-600 mt-2">
                Sign in to access your investment dashboard
              </p>
            </div>

            <form className="space-y-6" onSubmit={handleSubmit}>
              {/* Login ID */}
              <div>
                <label htmlFor="loginId" className="block text-sm font-semibold text-gray-700 mb-2">
                  Login ID
                </label>
                <input
                  id="loginId"
                  name="loginId"
                  type="text"
                  required
                  className={`w-full px-4 py-3 rounded-xl border-2 ${
                    errors.loginId 
                      ? 'border-red-300 focus:border-red-500 focus:ring-red-500' 
                      : 'border-gray-200 focus:border-blue-500 focus:ring-blue-500'
                  } text-gray-900 bg-gray-50 focus:bg-white focus:outline-none focus:ring-2 transition-all duration-200`}
                  placeholder="Enter your login ID"
                  value={formData.loginId}
                  onChange={handleChange}
                  disabled={isLoading}
                />
                {errors.loginId && (
                  <p className="mt-2 text-sm text-red-600 flex items-center">
                    <span className="w-1 h-1 bg-red-600 rounded-full mr-1"></span>
                    {errors.loginId}
                  </p>
                )}
              </div>

              {/* Password */}
              <div>
                <label htmlFor="password" className="block text-sm font-semibold text-gray-700 mb-2">
                  Password
                </label>
                <div className="relative">
                  <input
                    id="password"
                    name="password"
                    type={showPassword ? "text" : "password"}
                    required
                    className={`w-full px-4 py-3 pr-12 rounded-xl border-2 ${
                      errors.password 
                        ? 'border-red-300 focus:border-red-500 focus:ring-red-500' 
                        : 'border-gray-200 focus:border-blue-500 focus:ring-blue-500'
                    } text-gray-900 bg-gray-50 focus:bg-white focus:outline-none focus:ring-2 transition-all duration-200`}
                    placeholder="Enter your password"
                    value={formData.password}
                    onChange={handleChange}
                    disabled={isLoading}
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute right-4 top-1/2 transform -translate-y-1/2 text-gray-500 hover:text-gray-700 transition-colors"
                  >
                    {showPassword ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
                  </button>
                </div>
                {errors.password && (
                  <p className="mt-2 text-sm text-red-600 flex items-center">
                    <span className="w-1 h-1 bg-red-600 rounded-full mr-1"></span>
                    {errors.password}
                  </p>
                )}
              </div>

              {/* Success Message */}
              {successMessage && (
                <div className="rounded-xl bg-green-50 border-2 border-green-200 p-4">
                  <div className="flex items-center text-sm text-green-700">
                    <span className="w-2 h-2 bg-green-500 rounded-full mr-2"></span>
                    {successMessage}
                  </div>
                </div>
              )}

              {/* Error Message */}
              {errors.general && (
                <div className="rounded-xl bg-red-50 border-2 border-red-200 p-4">
                  <div className="flex items-center text-sm text-red-700">
                    <span className="w-2 h-2 bg-red-500 rounded-full mr-2"></span>
                    {errors.general}
                  </div>
                </div>
              )}

              {/* Submit Button */}
              <button
                type="submit"
                disabled={isLoading}
                className="w-full bg-gradient-to-r from-blue-600 via-purple-600 to-indigo-600 text-white py-3.5 rounded-xl font-semibold text-lg shadow-lg hover:shadow-xl transform hover:scale-[1.02] focus:outline-none focus:ring-4 focus:ring-blue-300 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none transition-all duration-200 flex items-center justify-center space-x-2"
              >
                {isLoading ? (
                  <>
                    <div className="animate-spin rounded-full h-5 w-5 border-2 border-white border-t-transparent"></div>
                    <span>Signing in...</span>
                  </>
                ) : (
                  <>
                    <span>Sign In</span>
                    <ArrowRight className="w-5 h-5" />
                  </>
                )}
              </button>

              {/* Switch to Signup */}
              <div className="text-center pt-4">
                <p className="text-gray-600 text-sm">
                  Don't have an account?{' '}
                  <button
                    type="button"
                    onClick={onSwitchToSignup}
                    className="font-semibold text-blue-600 hover:text-purple-600 transition-colors inline-flex items-center space-x-1"
                  >
                    <span>Create Account</span>
                    <ArrowRight className="w-4 h-4" />
                  </button>
                </p>
              </div>
            </form>
          </div>

          {/* Footer */}
          <p className="text-center text-gray-500 text-xs mt-6">
            Â© 2024 Stonks. Your trusted investment partner.
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
