// Type definitions for the application

export interface UserDashboardDTO {
  userID: string;
  userName: string;
  fundSummaries: FundSummaryDTO[];
  totalInvested: number;
  totalValue: number;
  profitLoss: number;
  profitLossPercent: number;
  xirr: number;
  totalEmergencyFundValue: number;
}

export interface FundSummaryDTO {
  name: string;
  fundId: number;
  tag: string;
  isEmergency: boolean;
  totalInvested: number;
  totalValue: number;
  totalUnits: number;
  profitLoss: number;
  profitLossPercent: number;
  xirr: number;
}

export interface MutualFundDTO {
  schemeCode: number;
  schemeName: string;
}

export interface ApiResponse<T> {
  data: T;
  success: boolean;
  message?: string;
}

export interface User {
  id: string;
  name: string;
  loginId: string;
  password: string;
}

export interface UserDTO {
  id?: string;
  name: string;
  loginId: string;
  password: string;
}
