import React, { useState } from 'react';
import { X, Coins, Calendar } from 'lucide-react';
import { sipAPI } from '../../services/api.js';

const SIPForm = ({ isOpen, onClose, fund, userId, onSuccess, sip = null }) => {
  const [formData, setFormData] = useState({
    amount: '',
    startDate: '',
    endDate: '',
    frequency: 'MONTHLY'
  });
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Initialize form data when editing
  React.useEffect(() => {
    if (sip) {
      setFormData({
        amount: sip.amount || '',
        startDate: sip.startDate ? sip.startDate.split('T')[0] : '',
        endDate: sip.endDate ? sip.endDate.split('T')[0] : '',
        frequency: sip.frequency || 'MONTHLY'
      });
    } else {
      setFormData({
        amount: '',
        startDate: '',
        endDate: '',
        frequency: 'MONTHLY'
      });
    }
  }, [sip]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.amount || formData.amount <= 0) {
      newErrors.amount = 'Amount is required and must be greater than 0';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      setIsSubmitting(true);

      const sipData = {
        fundName: fund.schemeName,
        fundId: fund.schemeCode,
        userId: userId,
        amount: parseFloat(formData.amount)
      };

      const response = await sipAPI.registerSIP(sipData);

      if (response && response.success) {
        onSuccess('SIP registered successfully!');
        handleClose();
      } else {
        setErrors({ general: response?.message || 'Failed to register SIP' });
      }
    } catch (error) {
      console.error('Error registering SIP:', error);
      setErrors({ general: 'Failed to register SIP. Please try again.' });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleClose = () => {
    setFormData({
      amount: ''
    });
    setErrors({});
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-xl max-w-md w-full mx-4">
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <h3 className="text-lg font-semibold text-gray-900">Register SIP</h3>
          <button
            onClick={handleClose}
            className="text-gray-400 hover:text-gray-600"
          >
            <X className="h-5 w-5" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="p-6">
          <div className="mb-4">
            <p className="text-sm text-gray-600 mb-2">Fund: <span className="font-medium">{fund?.schemeName}</span></p>
            <p className="text-xs text-gray-500">SIP will be registered for monthly investment</p>
          </div>

          {errors.general && (
            <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-md">
              <p className="text-sm text-red-600">{errors.general}</p>
            </div>
          )}

          <div className="space-y-4">
            {/* Amount Field */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Monthly SIP Amount (â‚¹)
              </label>
              <div className="relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Coins className="h-5 w-5 text-gray-400" />
                </div>
                <input
                  type="number"
                  name="amount"
                  value={formData.amount}
                  onChange={handleChange}
                  step="0.01"
                  min="0"
                  className={`block w-full pl-10 pr-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
                    errors.amount ? 'border-red-300' : 'border-gray-300'
                  }`}
                  placeholder="Enter monthly SIP amount"
                />
              </div>
              {errors.amount && (
                <p className="mt-1 text-sm text-red-600">{errors.amount}</p>
              )}
            </div>

            {/* Info Box */}
            <div className="bg-blue-50 border border-blue-200 rounded-md p-4">
              <div className="flex">
                <Calendar className="h-5 w-5 text-blue-400 mt-0.5 mr-3" />
                <div>
                  <h4 className="text-sm font-medium text-blue-800">SIP Information</h4>
                  <p className="text-sm text-blue-700 mt-1">
                    Your SIP will be automatically registered for monthly investments in this fund.
                    You can modify or cancel it later from your SIP management section.
                  </p>
                </div>
              </div>
            </div>
          </div>

          <div className="flex justify-end space-x-3 mt-6">
            <button
              type="button"
              onClick={handleClose}
              className="px-4 py-2 text-gray-700 bg-gray-100 rounded-lg hover:bg-gray-200"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={isSubmitting}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {isSubmitting ? 'Registering...' : 'Register SIP'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default SIPForm;
