import React from 'react';
// import { FundSummaryDTO } from '../../types/index';
import { TrendingUp, TrendingDown, Shield, Tag } from 'lucide-react';

const FundSummaryCard = ({ fund }) => {
  const isProfit = fund.profitLoss >= 0;
  const profitLossColor = isProfit ? 'text-green-600' : 'text-red-600';
  const profitLossBgColor = isProfit ? 'bg-green-50' : 'bg-red-50';

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatPercentage = (percentage) => {
    return `${percentage >= 0 ? '+' : ''}${percentage.toFixed(2)}%`;
  };

  return (
    <div className="bg-white rounded-lg shadow-sm border hover:shadow-md transition-shadow p-6">
      {/* Header */}
      <div className="flex items-start justify-between mb-4">
        <div className="flex-1">
          <h3 className="text-lg font-semibold text-gray-900 mb-1">{fund.name}</h3>
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">ID: {fund.fundId}</span>
            {fund.isEmergency && (
              <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
                <Shield className="w-3 h-3 mr-1" />
                Emergency
              </span>
            )}
          </div>
        </div>
        {fund.tag && (
          <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
            <Tag className="w-3 h-3 mr-1" />
            {fund.tag}
          </span>
        )}
      </div>

      {/* Investment Details */}
      <div className="space-y-3 mb-4">
        <div className="flex justify-between items-center">
          <span className="text-sm text-gray-600">Invested</span>
          <span className="font-medium text-gray-900">{formatCurrency(fund.totalInvested)}</span>
        </div>
        <div className="flex justify-between items-center">
          <span className="text-sm text-gray-600">Current Value</span>
          <span className="font-medium text-gray-900">{formatCurrency(fund.totalValue)}</span>
        </div>
        <div className="flex justify-between items-center">
          <span className="text-sm text-gray-600">Units</span>
          <span className="font-medium text-gray-900">{fund.totalUnits.toFixed(4)}</span>
        </div>
      </div>

      {/* Profit/Loss */}
      <div className={`p-3 rounded-lg ${profitLossBgColor} mb-4`}>
        <div className="flex items-center justify-between">
          <div className="flex items-center">
            {isProfit ? (
              <TrendingUp className="w-4 h-4 text-green-600 mr-2" />
            ) : (
              <TrendingDown className="w-4 h-4 text-red-600 mr-2" />
            )}
            <span className="text-sm font-medium text-gray-700">P&L</span>
          </div>
          <div className="text-right">
            <div className={`font-semibold ${profitLossColor}`}>
              {formatCurrency(Math.abs(fund.profitLoss))}
            </div>
            <div className={`text-sm ${profitLossColor}`}>
              {formatPercentage(fund.profitLossPercent)}
            </div>
          </div>
        </div>
      </div>

      {/* XIRR */}
      <div className="flex justify-between items-center pt-3 border-t border-gray-200">
        <span className="text-sm text-gray-600">XIRR</span>
        <span className="font-semibold text-gray-900">
          {fund.xirr.toFixed(2)}%
        </span>
      </div>
    </div>
  );
};

export default FundSummaryCard;
