import React, { useState, useEffect } from 'react';
import { ArrowLeft, Plus, Calendar, TrendingUp, DollarSign, PieChart, Clock } from 'lucide-react';
import { userFundAPI } from '../../services/api.js';
import LoadingSpinner from '../common/LoadingSpinner.jsx';
import ErrorMessage from '../common/ErrorMessage.jsx';

const UserFundDetail = ({ fund, userId, onBack, onRecordTransaction, onRegisterSIP }) => {
  const [fundDetails, setFundDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (fund && userId) {
      loadFundDetails();
    }
  }, [fund, userId]);

  const loadFundDetails = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await userFundAPI.getUserFundDetails(userId, fund.schemeCode);

      if (response && response.success) {
        setFundDetails(response.data);
      } else {
        setError(response?.message || 'Failed to load fund details');
      }
    } catch (err) {
      console.error('Error loading fund details:', err);
      setError('Failed to load fund details. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 2,
    }).format(amount || 0);
  };

  const formatDate = (timestamp) => {
    return new Date(timestamp * 1000).toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const formatPercentage = (value) => {
    return `${(value || 0).toFixed(2)}%`;
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <LoadingSpinner />
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <ErrorMessage message={error} onClose={() => setError(null)} />
          <button
            onClick={onBack}
            className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Go Back
          </button>
        </div>
      </div>
    );
  }

  if (!fundDetails) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="text-gray-500 text-lg mb-4">No fund details found</div>
          <button
            onClick={onBack}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Go Back
          </button>
        </div>
      </div>
    );
  }

  const { userFundDTO, summary, units, registeredSIPs } = fundDetails;

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center">
              <button
                onClick={onBack}
                className="mr-4 p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg"
              >
                <ArrowLeft className="h-5 w-5" />
              </button>
              <div>
                <h1 className="text-xl font-semibold text-gray-900">{fund.schemeName}</h1>
                <p className="text-sm text-gray-500">Scheme Code: {fund.schemeCode}</p>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="flex space-x-3">
              <button
                onClick={() => onRecordTransaction(fund)}
                className="flex items-center px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
              >
                <Plus className="h-4 w-4 mr-2" />
                Record Transaction
              </button>
              <button
                onClick={() => onRegisterSIP(fund)}
                className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
              >
                <Calendar className="h-4 w-4 mr-2" />
                Register SIP
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Fund Summary Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
            <div className="flex items-center">
              <div className="p-2 bg-blue-100 rounded-lg">
                <DollarSign className="h-6 w-6 text-blue-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">Total Invested</p>
                <p className="text-2xl font-semibold text-gray-900">
                  {formatCurrency(summary?.totalInvested)}
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
            <div className="flex items-center">
              <div className="p-2 bg-green-100 rounded-lg">
                <TrendingUp className="h-6 w-6 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">Current Value</p>
                <p className="text-2xl font-semibold text-gray-900">
                  {formatCurrency(summary?.totalValue)}
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
            <div className="flex items-center">
              <div className={`p-2 rounded-lg ${(summary?.profitLoss || 0) >= 0 ? 'bg-green-100' : 'bg-red-100'}`}>
                <PieChart className={`h-6 w-6 ${(summary?.profitLoss || 0) >= 0 ? 'text-green-600' : 'text-red-600'}`} />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">Profit/Loss</p>
                <p className={`text-2xl font-semibold ${(summary?.profitLoss || 0) >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                  {formatCurrency(summary?.profitLoss)}
                </p>
                <p className={`text-sm ${(summary?.profitLossPercent || 0) >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                  {formatPercentage(summary?.profitLossPercent)}
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
            <div className="flex items-center">
              <div className="p-2 bg-purple-100 rounded-lg">
                <Clock className="h-6 w-6 text-purple-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">XIRR</p>
                <p className="text-2xl font-semibold text-gray-900">
                  {formatPercentage(summary?.xirr)}
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* Fund Information */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
          {/* Fund Details */}
          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Fund Information</h3>
            <div className="space-y-3">
              <div className="flex justify-between">
                <span className="text-gray-500">Fund Name:</span>
                <span className="font-medium">{userFundDTO?.fundName}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-500">Fund ID:</span>
                <span className="font-medium">{userFundDTO?.fundId}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-500">Tag:</span>
                <span className="font-medium">{userFundDTO?.tag || 'No tag'}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-500">Emergency Fund:</span>
                <span className={`font-medium ${userFundDTO?.isEmergency ? 'text-red-600' : 'text-green-600'}`}>
                  {userFundDTO?.isEmergency ? 'Yes' : 'No'}
                </span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-500">Total Units:</span>
                <span className="font-medium">{summary?.totalUnits?.toFixed(4) || '0.0000'}</span>
              </div>
            </div>
          </div>

          {/* Registered SIPs */}
          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Registered SIPs</h3>
            {registeredSIPs && registeredSIPs.length > 0 ? (
              <div className="space-y-3">
                {registeredSIPs.map((sip, index) => (
                  <div key={sip.id || index} className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                    <div>
                      <p className="font-medium text-gray-900">{sip.fundName}</p>
                      <p className="text-sm text-gray-500">SIP ID: {sip.id}</p>
                    </div>
                    <div className="text-right">
                      <p className="font-semibold text-gray-900">{formatCurrency(sip.amount)}</p>
                      <p className="text-sm text-gray-500">per month</p>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center py-8">
                <Calendar className="h-12 w-12 text-gray-300 mx-auto mb-4" />
                <p className="text-gray-500">No SIPs registered</p>
                <p className="text-sm text-gray-400">Click "Register SIP" to set up a new SIP</p>
              </div>
            )}
          </div>
        </div>

        {/* Units History */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200">
          <div className="px-6 py-4 border-b border-gray-200">
            <h3 className="text-lg font-semibold text-gray-900">Units History</h3>
          </div>
          <div className="overflow-x-auto">
            {units && units.length > 0 ? (
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Date
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Amount
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Units
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      P&L
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      P&L %
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {units.map((unit, index) => (
                    <tr key={index} className="hover:bg-gray-50">
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {formatDate(unit.date)}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {formatCurrency(unit.amount)}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {unit.units?.toFixed(4)}
                      </td>
                      <td className={`px-6 py-4 whitespace-nowrap text-sm font-medium ${
                        (unit.profitLoss || 0) >= 0 ? 'text-green-600' : 'text-red-600'
                      }`}>
                        {formatCurrency(unit.profitLoss)}
                      </td>
                      <td className={`px-6 py-4 whitespace-nowrap text-sm font-medium ${
                        (unit.profitLossPercent || 0) >= 0 ? 'text-green-600' : 'text-red-600'
                      }`}>
                        {formatPercentage(unit.profitLossPercent)}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <div className="text-center py-12">
                <PieChart className="h-12 w-12 text-gray-300 mx-auto mb-4" />
                <p className="text-gray-500">No units history available</p>
                <p className="text-sm text-gray-400">Record transactions to see units history</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserFundDetail;
