import React from 'react';

const LoadingSpinner = () => {
  return (
    <div className="bg-gray-50 flex items-center justify-center py-24">
      <div className="text-center">
        {/* Animated icon with pulse effect */}
        <div className="relative mx-auto mb-6">
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="rounded-full bg-green-100 animate-ping w-20 h-20 opacity-75"></div>
          </div>
          <div className="relative rounded-full bg-gradient-to-br from-green-500 to-emerald-600 p-4 shadow-lg">
            <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
          </div>
        </div>

        {/* Text with animation */}
        <div className="space-y-2">
          <h2 className="text-2xl font-bold text-gray-900 animate-pulse">Loading Data and Creating Wealth</h2>
          <p className="text-gray-500 text-sm">Please wait...</p>
        </div>

        {/* Progress dots */}
        <div className="flex items-center justify-center space-x-2 mt-6">
          <div className="w-2 h-2 bg-green-500 rounded-full animate-bounce" style={{ animationDelay: '0ms' }}></div>
          <div className="w-2 h-2 bg-green-500 rounded-full animate-bounce" style={{ animationDelay: '150ms' }}></div>
          <div className="w-2 h-2 bg-green-500 rounded-full animate-bounce" style={{ animationDelay: '300ms' }}></div>
        </div>
      </div>
    </div>
  );
};

export default LoadingSpinner;
