import React, { useState, useEffect } from 'react';
import { dashboardAPI, userFundAPI, transactionAPI, userAPI } from '../../services/api.js';
import DashboardNavigation from './DashboardNavigation.jsx';
import FundSearch from './FundSearch.jsx';
import FundSummaryCard from './FundSummaryCard.jsx';
import PortfolioSummary from './PortfolioSummary.jsx';
import UserFundDetail from './UserFundDetail.jsx';
import LoadingSpinner from '../common/LoadingSpinner.jsx';
import ErrorMessage from '../common/ErrorMessage.jsx';

const UserDashboard = ({ user, onLogout }) => {
  const [currentView, setCurrentView] = useState('dashboard');
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchResults, setSearchResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [selectedFund, setSelectedFund] = useState(null);

  useEffect(() => {
    if (user?.id) {
      loadDashboardData();
    }
  }, [user]);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await dashboardAPI.getUserDashboard(user.id);

      if (response && response.success) {
        setDashboardData(response.data);
      } else {
        setError(response?.message || 'Failed to load dashboard data');
      }
    } catch (err) {
      console.error('Error loading dashboard:', err);
      setError('Failed to load dashboard data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (searchText) => {
    if (!searchText.trim()) {
      setSearchResults([]);
      return;
    }

    try {
      setIsSearching(true);
      const response = await dashboardAPI.searchFunds(searchText);
      if (response && response.success) {
        setSearchResults(response.data || []);
      } else {
        setSearchResults([]);
      }
    } catch (err) {
      console.error('Error searching funds:', err);
      setSearchResults([]);
    } finally {
      setIsSearching(false);
    }
  };

  const handleLogout = () => {
    if (window.confirm('Are you sure you want to logout?')) {
      onLogout();
    }
  };

  const handleFundSelect = (fund) => {
    setSelectedFund(fund);
  };

  const handleBackToDashboard = () => {
    setSelectedFund(null);
  };

  const handleRecordTransaction = (fund) => {
    // TODO: Implement transaction recording modal/page
    alert(`Record transaction for ${fund.schemeName} - Feature coming soon!`);
  };

  const handleRegisterSIP = (fund) => {
    // TODO: Implement SIP registration modal/page
    alert(`Register SIP for ${fund.schemeName} - Feature coming soon!`);
  };

  const renderCurrentView = () => {
    switch (currentView) {
      case 'transactions':
        return (
          <div className="text-center py-12">
            <div className="text-gray-500 text-lg mb-2">Transaction Management</div>
            <div className="text-gray-400">Transaction features coming soon...</div>
          </div>
        );
      case 'funds':
        return (
          <div className="text-center py-12">
            <div className="text-gray-500 text-lg mb-2">Fund Details</div>
            <div className="text-gray-400">Fund details features coming soon...</div>
          </div>
        );
      case 'profile':
        return (
          <div className="text-center py-12">
            <div className="text-gray-500 text-lg mb-2">User Profile</div>
            <div className="text-gray-400">Profile management coming soon...</div>
          </div>
        );
      case 'dashboard':
      default:
        return renderDashboard();
    }
  };

  const renderDashboard = () => (
    <>
      {/* Search Section */}
      <div className="mb-8">
        <h2 className="text-2xl font-bold text-gray-900 mb-4">Search Mutual Funds</h2>
        <FundSearch onSearch={handleSearch} />
        {isSearching && (
          <div className="mt-4 flex items-center justify-center">
            <LoadingSpinner />
          </div>
        )}
        {searchResults.length > 0 && (
          <div className="mt-4">
            <h3 className="text-lg font-medium text-gray-900 mb-2">Search Results</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {searchResults.map((fund) => (
                <div 
                  key={fund.schemeCode} 
                  className="bg-white p-4 rounded-lg shadow-sm border border-gray-200 hover:shadow-md hover:border-blue-300 cursor-pointer transition-all duration-200"
                  onClick={() => handleFundSelect(fund)}
                >
                  <h4 className="font-medium text-gray-900">{fund.schemeName}</h4>
                  <p className="text-xs text-blue-600 mt-2">Click to view details</p>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Dashboard Content */}
      {dashboardData ? (
        <div className="space-y-8">
          {/* Portfolio Summary */}
          <PortfolioSummary dashboardData={dashboardData} />

          {/* Fund Summaries */}
          {dashboardData.fundSummaries && dashboardData.fundSummaries.length > 0 && (
            <div>
              <h2 className="text-2xl font-bold text-gray-900 mb-4">Your Investments</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {dashboardData.fundSummaries.map((fund) => (
                  <FundSummaryCard key={fund.fundId} fund={fund} />
                ))}
              </div>
            </div>
          )}

          {/* Empty State */}
          {(!dashboardData.fundSummaries || dashboardData.fundSummaries.length === 0) && (
            <div className="text-center py-12">
              <div className="text-gray-500 text-lg mb-2">No investments found</div>
              <div className="text-gray-400">Start by searching for mutual funds above</div>
            </div>
          )}
        </div>
      ) : (
        <div className="text-center py-12">
          <div className="text-gray-500 text-lg mb-2">Welcome to your dashboard!</div>
          <div className="text-gray-400">Search for mutual funds to get started</div>
        </div>
      )}
    </>
  );

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <LoadingSpinner />
      </div>
    );
  }

  // Show UserFundDetail if a fund is selected
  if (selectedFund) {
    return (
      <UserFundDetail
        fund={selectedFund}
        userId={user.id}
        onBack={handleBackToDashboard}
        onRecordTransaction={handleRecordTransaction}
        onRegisterSIP={handleRegisterSIP}
      />
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Navigation */}
      <DashboardNavigation 
        currentView={currentView}
        onViewChange={setCurrentView}
        onLogout={handleLogout}
        user={user}
      />

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {error && (
          <div className="mb-6">
            <ErrorMessage message={error} onClose={() => setError(null)} />
          </div>
        )}

        {renderCurrentView()}
      </main>
    </div>
  );
};

export default UserDashboard;
