import React from 'react';

const LoadingSpinner = () => {
  return (
    <div className="bg-gray-50 flex items-center justify-center py-24">
      <div className="text-center">
        {/* Rotating ring animation */}
        <div className="relative mx-auto mb-8 w-24 h-24">
          {/* Outer rotating ring */}
          <div className="absolute inset-0">
            <div className="w-full h-full border-4 border-gray-200 border-t-green-500 rounded-full animate-spin"></div>
          </div>

          {/* Middle ring */}
          <div className="absolute inset-4">
            <div className="w-full h-full border-4 border-gray-200 border-b-green-500 rounded-full animate-spin" style={{ animationDirection: 'reverse', animationDuration: '1.5s' }}></div>
          </div>

          {/* Inner ring */}
          <div className="absolute inset-8">
            <div className="w-full h-full border-2 border-gray-200 border-r-green-400 rounded-full animate-spin" style={{ animationDuration: '2s' }}></div>
          </div>

          {/* Center icon */}
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="w-8 h-8 rounded-full bg-gradient-to-br from-green-500 to-emerald-600 flex items-center justify-center shadow-md">
              <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
              </svg>
            </div>
          </div>
        </div>

        {/* Text with gradient animation */}
        <div className="space-y-2">
          <h2 className="text-2xl font-bold bg-gradient-to-r from-green-600 via-emerald-600 to-green-600 bg-clip-text text-transparent bg-[length:200%_auto] animate-[shimmer_2s_linear_infinite]">
            Loading Data and Creating Wealth
          </h2>
          <p className="text-gray-500 text-sm">Please wait...</p>
        </div>

        {/* Animated progress bar */}
        <div className="mt-8 w-64 mx-auto">
          <div className="h-1 bg-gray-200 rounded-full overflow-hidden">
            <div className="h-full bg-gradient-to-r from-green-500 to-emerald-600 rounded-full animate-[loading_2s_ease-in-out_infinite]" style={{ width: '100%' }}></div>
          </div>
        </div>
      </div>

      {/* Add custom animations */}
      <style jsx>{`
        @keyframes shimmer {
          0% { background-position: 0% 50%; }
          100% { background-position: 200% 50%; }
        }

        @keyframes loading {
          0% { width: 0%; }
          50% { width: 70%; }
          100% { width: 0%; }
        }
      `}</style>
    </div>
  );
};

export default LoadingSpinner;
