import React from 'react';
import { ArrowUpDown, ArrowUp, ArrowDown } from 'lucide-react';

const FundSorting = ({ orderBy, orderDirection, onSortChange }) => {
  const sortingOptions = [
    { value: 'TOTAL_VALUE', label: 'Total Value' },
    { value: 'PROFIT_PERCENT', label: 'Profit %' },
    { value: 'TAG', label: 'Tag' },
    { value: 'FUND_NAME', label: 'Fund Name' },
    { value: 'TOTAL_INVESTED', label: 'Total Invested' },
    { value: 'PROFIT_LOSS', label: 'Profit/Loss' }
  ];

  const handleSortChange = (newOrderBy) => {
    let newOrderDirection = 'DESC';

    // If clicking the same field, toggle direction
    if (orderBy === newOrderBy) {
      newOrderDirection = orderDirection === 'ASC' ? 'DESC' : 'ASC';
    }

    onSortChange(newOrderBy, newOrderDirection);
  };

  const getSortIcon = (optionValue) => {
    if (orderBy !== optionValue) {
      return <ArrowUpDown className="w-4 h-4 text-gray-400" />;
    }
    return orderDirection === 'ASC' 
      ? <ArrowUp className="w-4 h-4 text-blue-600" />
      : <ArrowDown className="w-4 h-4 text-blue-600" />;
  };

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4 mb-6">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-gray-900 flex items-center">
          <ArrowUpDown className="w-5 h-5 mr-2 text-gray-600" />
          Sort Funds
        </h3>
        <div className="text-sm text-gray-500">
          Click on any column to sort
        </div>
      </div>

      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-6 gap-3">
        {sortingOptions.map((option) => (
          <button
            key={option.value}
            onClick={() => handleSortChange(option.value)}
            className={`flex items-center justify-between p-3 rounded-lg border transition-all duration-200 hover:shadow-md ${
              orderBy === option.value
                ? 'border-blue-500 bg-blue-50 text-blue-900'
                : 'border-gray-200 bg-white text-gray-700 hover:border-gray-300'
            }`}
          >
            <span className="text-sm font-medium">{option.label}</span>
            {getSortIcon(option.value)}
          </button>
        ))}
      </div>

      <div className="mt-3 text-xs text-gray-500">
        Current sort: <span className="font-medium">{sortingOptions.find(opt => opt.value === orderBy)?.label}</span> 
        {' '}({orderDirection === 'ASC' ? 'Ascending' : 'Descending'})
      </div>
    </div>
  );
};

export default FundSorting;
