import React, { useState, useEffect, useCallback } from 'react';
import { 
  ArrowLeft, 
  Plus, 
  Calendar, 
  TrendingUp, 
  Coins, 
  PieChart, 
  Layers,
  Shield,
  Activity,
  BarChart3,
  Zap,
  RefreshCw,
  Edit3,
  Save,
  X
} from 'lucide-react';
import { userFundAPI, transactionAPI } from '../../services/api.js';
import HistoricDataModal from './HistoricDataModal.jsx';
import LoadingSpinner from '../common/LoadingSpinner.jsx';
import ErrorMessage from '../common/ErrorMessage.jsx';
import TransactionForm from './TransactionForm.jsx';
import SIPForm from './SIPForm.jsx';
import BulkTransactionForm from './BulkTransactionForm.jsx';
import UnitsRow from '../common/UnitsRow.jsx';

const UserFundDetail = ({ fund, userId, onBack, onRecordTransaction, onRegisterSIP }) => {
  const [fundDetails, setFundDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showTransactionForm, setShowTransactionForm] = useState(false);
  const [showBulkTransactionForm, setShowBulkTransactionForm] = useState(false);
  const [showSIPForm, setShowSIPForm] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');
  const [refreshing, setRefreshing] = useState(false);
  const [showDeleteDialog, setShowDeleteDialog] = useState(false);
  const [transactionToDelete, setTransactionToDelete] = useState(null);
  const [isDeleting, setIsDeleting] = useState(false);
  const [dialogError, setDialogError] = useState(null);
  const [isEditingTag, setIsEditingTag] = useState(false);
  const [tagValue, setTagValue] = useState('');
  const [isUpdating, setIsUpdating] = useState(false);
  const [showHistoricDataModal, setShowHistoricDataModal] = useState(false);

  const loadFundDetails = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await userFundAPI.getUserFundDetails(userId, fund.schemeCode);

      console.log('Fund Details Response:', response);

      if (response) {
        setFundDetails(response);
      } else {
        setError('Failed to load fund details');
      }
    } catch (err) {
      console.error('Error loading fund details:', err);
      setError('Failed to load fund details. Please try again.');
    } finally {
      setLoading(false);
    }
  }, [userId, fund.schemeCode]);

  useEffect(() => {
    if (fund && userId) {
      loadFundDetails();
    }
  }, [fund, userId, loadFundDetails]);

  const handleRefresh = async () => {
    setRefreshing(true);
    await loadFundDetails();
    setRefreshing(false);
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 2,
    }).format(amount || 0);
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Invalid Date';

    try {
      // Handle string format "dd-MM-yyyy"
      if (typeof dateString === 'string') {
        // Parse the dd-MM-yyyy format
        const parts = dateString.split('-');
        if (parts.length === 3) {
          const day = parts[0];
          const month = parts[1];
          const year = parts[2];

          // Create date object (month is 0-indexed in JavaScript)
          const date = new Date(year, month - 1, day);

          // Check if date is valid
          if (isNaN(date.getTime())) {
            return 'Invalid Date';
          }

          return date.toLocaleDateString('en-IN', {
            year: 'numeric',
            month: 'short',
            day: 'numeric'
          });
        }
      }

      // Fallback for timestamp format (if needed)
      let date;
      if (typeof dateString === 'number') {
        if (dateString > 10000000000) {
          // Timestamp is in milliseconds
          date = new Date(dateString);
        } else {
          // Timestamp is in seconds
          date = new Date(dateString * 1000);
        }
      } else {
        date = new Date(dateString);
      }

      // Check if date is valid
      if (isNaN(date.getTime())) {
        return 'Invalid Date';
      }

      return date.toLocaleDateString('en-IN', {
        year: 'numeric',
        month: 'short',
        day: 'numeric'
      });
    } catch (error) {
      console.error('Error formatting date:', error);
      return 'Invalid Date';
    }
  };

  const formatPercentage = (value) => {
    if (value == null || isNaN(value)) {
      return 'N/A';
    }
    return `${value.toFixed(2)}%`;
  };

  const handleSuccess = (message) => {
    setSuccessMessage(message);
    // Reload fund details to show updated data
    loadFundDetails();
    // Clear success message after 3 seconds
    setTimeout(() => setSuccessMessage(''), 3000);
  };

  const handleDeleteTransaction = (unit) => {
    if (!unit.transactionId) {
      setError('Cannot delete transaction: No transaction ID found');
      return;
    }

    setTransactionToDelete(unit);
    setDialogError(null);
    setShowDeleteDialog(true);
  };

  const confirmDeleteTransaction = async () => {
    if (!transactionToDelete) return;

    try {
      setIsDeleting(true);
      setDialogError(null);
      setError(null);
      await transactionAPI.deleteTransaction(transactionToDelete.transactionId);
      setSuccessMessage('Transaction deleted successfully');
      // Reload fund details to show updated data
      await loadFundDetails();
      // Clear success message after 5 seconds
      setTimeout(() => setSuccessMessage(''), 5000);
      // Close dialog on success
      setShowDeleteDialog(false);
      setTransactionToDelete(null);
    } catch (err) {
      console.error('Error deleting transaction:', err);

      // Extract error message from API response
      let errorMessage = 'Please try again.';

      if (err.response?.data?.message) {
        // API returned a structured error response
        errorMessage = err.response.data.message;
      } else if (err.message) {
        // Generic error message
        errorMessage = err.message;
      } else if (typeof err === 'string') {
        // String error
        errorMessage = err;
      }

      setDialogError(errorMessage);
    } finally {
      setIsDeleting(false);
    }
  };

  const cancelDeleteTransaction = () => {
    setShowDeleteDialog(false);
    setTransactionToDelete(null);
    setIsDeleting(false);
    setDialogError(null);
  };

  const handleEmergencyToggle = async () => {
    if (!fundDetails?.userFundDTO) return;

    try {
      setIsUpdating(true);
      const currentEmergencyStatus = fundDetails.userFundDTO.isEmergency;
      const updateData = {
        userId: userId,
        fundId: fund.schemeCode,
        fundName: fund.schemeName,
        isEmergency: !currentEmergencyStatus,
        tag: fundDetails.userFundDTO.tag || ''
      };

      await userFundAPI.updateUserFund(userId, fund.schemeCode, updateData);

      // Update local state instead of reloading
      setFundDetails(prev => ({
        ...prev,
        userFundDTO: {
          ...prev.userFundDTO,
          isEmergency: !currentEmergencyStatus
        }
      }));

      setSuccessMessage(`Fund ${!currentEmergencyStatus ? 'marked as' : 'removed from'} emergency fund`);
      setTimeout(() => setSuccessMessage(''), 5000);
    } catch (error) {
      console.error('Error updating emergency status:', error);
      setError('Failed to update emergency status. Please try again.');
    } finally {
      setIsUpdating(false);
    }
  };

  const handleTagEdit = () => {
    setTagValue(fundDetails?.userFundDTO?.tag || '');
    setIsEditingTag(true);
  };

  const handleTagSave = async () => {
    if (!fundDetails?.userFundDTO) return;

    try {
      setIsUpdating(true);
      const updateData = {
        userId: userId,
        fundId: fund.schemeCode,
        fundName: fund.schemeName,
        isEmergency: fundDetails.userFundDTO.isEmergency,
        tag: tagValue.trim()
      };

      await userFundAPI.updateUserFund(userId, fund.schemeCode, updateData);

      // Update local state instead of reloading
      setFundDetails(prev => ({
        ...prev,
        userFundDTO: {
          ...prev.userFundDTO,
          tag: tagValue.trim()
        }
      }));

      setSuccessMessage('Tag updated successfully');
      setIsEditingTag(false);
      setTimeout(() => setSuccessMessage(''), 5000);
    } catch (error) {
      console.error('Error updating tag:', error);
      setError('Failed to update tag. Please try again.');
    } finally {
      setIsUpdating(false);
    }
  };

  const handleTagCancel = () => {
    setIsEditingTag(false);
    setTagValue('');
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50 flex items-center justify-center">
        <div className="text-center">
          <LoadingSpinner />
          <p className="mt-4 text-gray-600">Loading fund details...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50 flex items-center justify-center">
        <div className="text-center">
          <ErrorMessage message={error} onClose={() => setError(null)} />
          <button
            onClick={onBack}
            className="mt-4 px-6 py-3 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-lg hover:from-blue-700 hover:to-purple-700 transition-all duration-300 transform hover:scale-105"
          >
            Go Back
          </button>
        </div>
      </div>
    );
  }

  if (!fundDetails) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50 flex items-center justify-center">
        <div className="text-center">
          <div className="bg-white p-8 rounded-xl shadow-lg border border-gray-200">
            <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <PieChart className="w-8 h-8 text-gray-400" />
            </div>
            <div className="text-gray-500 text-lg mb-4">No fund details found</div>
            <button
              onClick={onBack}
              className="px-6 py-3 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-lg hover:from-blue-700 hover:to-purple-700 transition-all duration-300 transform hover:scale-105"
            >
              Go Back
            </button>
          </div>
        </div>
      </div>
    );
  }

  const { userFundDTO, summary, extraSummary, units, registeredSIPs } = fundDetails;

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50">
      {/* Enhanced Header */}
      <div className="bg-white shadow-lg border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-20">
            <div className="flex items-center space-x-4">
              <button
                onClick={onBack}
                className="p-3 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-xl transition-all duration-300 hover:scale-105"
              >
                <ArrowLeft className="h-6 w-6" />
              </button>
              <div className="flex items-center space-x-3">
                <div className="p-3 bg-gradient-to-r from-blue-600 to-purple-600 rounded-xl">
                  <PieChart className="h-6 w-6 text-white" />
                </div>
                <div>
                  <h1 className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
                    {fund.schemeName}
                  </h1>
                  <div className="flex items-center space-x-2">
                    {fundDetails?.latestNav ? (
                      <span className="inline-flex items-center px-2 py-1 rounded-md text-sm font-semibold bg-blue-50 text-blue-700 border border-blue-200">
                        Latest NAV: {fundDetails.latestNav.toFixed(2)}
                      </span>
                    ) : (
                      <span className="inline-flex items-center px-2 py-1 rounded-md text-sm font-semibold bg-gray-50 text-gray-600 border border-gray-200">
                        NAV: N/A
                      </span>
                    )}
                    {fundDetails?.latestNavDate && (
                      <span className="inline-flex items-center px-2 py-1 rounded-md text-sm font-semibold bg-green-50 text-green-700 border border-green-200">
                        {formatDate(fundDetails.latestNavDate)}
                      </span>
                    )}
                  </div>
                </div>
              </div>
            </div>

            {/* Enhanced Action Buttons */}
            <div className="flex items-center space-x-3">
              <button
                onClick={handleRefresh}
                disabled={refreshing}
                className="flex items-center px-4 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200 transition-all duration-300 transform hover:scale-105 disabled:opacity-50"
              >
                <RefreshCw className={`h-4 w-4 mr-2 ${refreshing ? 'animate-spin' : ''}`} />
                {refreshing ? 'Refreshing...' : 'Refresh'}
              </button>
              <button
                onClick={() => setShowTransactionForm(true)}
                className="flex items-center px-4 py-2 bg-gradient-to-r from-green-600 to-emerald-600 text-white rounded-lg hover:from-green-700 hover:to-emerald-700 transition-all duration-300 transform hover:scale-105"
              >
                <Plus className="h-4 w-4 mr-2" />
                Record Transaction
              </button>
              <button
                onClick={() => setShowBulkTransactionForm(true)}
                className="flex items-center px-4 py-2 bg-gradient-to-r from-purple-600 to-indigo-600 text-white rounded-lg hover:from-purple-700 hover:to-indigo-700 transition-all duration-300 transform hover:scale-105"
              >
                <Layers className="h-4 w-4 mr-2" />
                Bulk Transactions
              </button>
              <button
                onClick={() => setShowSIPForm(true)}
                className="flex items-center px-4 py-2 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-lg hover:from-blue-700 hover:to-purple-700 transition-all duration-300 transform hover:scale-105"
              >
                <Calendar className="h-4 w-4 mr-2" />
                Register SIP
              </button>
              <button
                onClick={() => setShowHistoricDataModal(true)}
                className="flex items-center px-4 py-2 bg-gradient-to-r from-indigo-600 to-pink-600 text-white rounded-lg hover:from-indigo-700 hover:to-pink-700 transition-all duration-300 transform hover:scale-105"
              >
                <BarChart3 className="h-4 w-4 mr-2" />
                View Historic Data
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Success Message */}
        {successMessage && (
          <div className="mb-6 p-4 bg-gradient-to-r from-green-50 to-emerald-50 border border-green-200 rounded-xl shadow-sm animate-fade-in">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <div className="w-8 h-8 bg-green-500 rounded-full flex items-center justify-center">
                  <svg className="h-5 w-5 text-white" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                  </svg>
                </div>
              </div>
              <div className="ml-3">
                <p className="text-sm font-medium text-green-800">{successMessage}</p>
              </div>
              <div className="ml-auto">
                <button
                  onClick={() => setSuccessMessage('')}
                  className="text-green-400 hover:text-green-600 focus:outline-none"
                >
                  <svg className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
                  </svg>
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Error Message */}
        {error && (
          <div className="mb-6 p-4 bg-gradient-to-r from-red-50 to-red-100 border border-red-200 rounded-xl shadow-sm animate-fade-in">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <div className="w-8 h-8 bg-red-500 rounded-full flex items-center justify-center">
                  <svg className="h-5 w-5 text-white" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                  </svg>
                </div>
              </div>
              <div className="ml-3">
                <p className="text-sm font-medium text-red-800">{error}</p>
              </div>
              <div className="ml-auto">
                <button
                  onClick={() => setError(null)}
                  className="text-red-400 hover:text-red-600 focus:outline-none"
                >
                  <svg className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
                  </svg>
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Enhanced Fund Summary Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-6 mb-8">
          <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-200 hover:shadow-xl transition-all duration-300 hover:scale-105 group">
            <div className="flex items-center justify-between">
                <div className="p-3 bg-gradient-to-r from-blue-500 to-blue-600 rounded-xl group-hover:scale-110 transition-transform duration-300">
                  <Coins className="h-6 w-6 text-white" />
              </div>
              <div className="text-right">
                <p className="text-sm font-medium text-gray-600 mb-1">Total Invested</p>
                <p className="text-2xl font-bold text-blue-600">
                  {formatCurrency(summary?.totalInvested)}
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-200 hover:shadow-xl transition-all duration-300 hover:scale-105 group">
            <div className="flex items-center justify-between">
              <div className="p-3 bg-gradient-to-r from-green-500 to-green-600 rounded-xl group-hover:scale-110 transition-transform duration-300">
                <TrendingUp className="h-6 w-6 text-white" />
              </div>
              <div className="text-right">
                <p className="text-sm font-medium text-gray-600 mb-1">Current Value</p>
                <p className="text-2xl font-bold text-green-600">
                  {formatCurrency(summary?.totalValue)}
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-200 hover:shadow-xl transition-all duration-300 hover:scale-105 group">
            <div className="flex items-center justify-between">
              <div className={`p-3 rounded-xl group-hover:scale-110 transition-transform duration-300 ${
                (summary?.profitLoss || 0) >= 0 ? 'bg-gradient-to-r from-green-500 to-green-600' : 'bg-gradient-to-r from-red-500 to-red-600'
              }`}>
                <PieChart className={`h-6 w-6 ${
                  (summary?.profitLoss || 0) >= 0 ? 'text-white' : 'text-white'
                }`} />
              </div>
              <div className="text-right">
                <p className="text-sm font-medium text-gray-600 mb-1">Profit/Loss</p>
                <p className={`text-2xl font-bold ${
                  (summary?.profitLoss || 0) >= 0 ? 'text-green-600' : 'text-red-600'
                }`}>
                  {formatCurrency(summary?.profitLoss)}
                </p>
                <p className={`text-sm font-medium ${
                  (summary?.profitLossPercent || 0) >= 0 ? 'text-green-600' : 'text-red-600'
                }`}>
                  {formatPercentage(summary?.profitLossPercent)}
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-200 hover:shadow-xl transition-all duration-300 hover:scale-105 group">
            <div className="flex items-center justify-between">
              <div className="p-3 bg-gradient-to-r from-purple-500 to-purple-600 rounded-xl group-hover:scale-110 transition-transform duration-300">
                <Activity className="h-6 w-6 text-white" />
              </div>
              <div className="text-right">
                <p className="text-sm font-medium text-gray-600 mb-1">XIRR</p>
                <p className="text-2xl font-bold text-purple-600">
                  {formatPercentage(extraSummary?.xirr)}
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-200 hover:shadow-xl transition-all duration-300 hover:scale-105 group">
            <div className="flex items-center justify-between">
              <div className="p-3 bg-gradient-to-r from-violet-500 to-violet-600 rounded-xl group-hover:scale-110 transition-transform duration-300">
                <TrendingUp className="h-6 w-6 text-white" />
              </div>
              <div className="text-right">
                <p className="text-sm font-medium text-gray-600 mb-1">Long Term Gains</p>
                <p className="text-2xl font-bold text-violet-600">
                  {formatCurrency(extraSummary?.longTermGains)}
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* Enhanced Fund Information */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
          {/* Fund Details */}
          <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-200">
            <div className="flex items-center mb-6">
              <BarChart3 className="w-6 h-6 mr-3 text-blue-600" />
              <h3 className="text-xl font-bold text-gray-900">Fund Information</h3>
            </div>
            <div className="space-y-4">
              <div className="flex justify-between items-center p-3 bg-gradient-to-r from-gray-50 to-gray-100 rounded-lg">
                <span className="text-gray-600 font-medium">Fund Name:</span>
                <span className="font-semibold text-gray-900">{userFundDTO?.fundName}</span>
              </div>
              <div className="flex justify-between items-center p-3 bg-gradient-to-r from-gray-50 to-gray-100 rounded-lg">
                <span className="text-gray-600 font-medium">Tag:</span>
                {isEditingTag ? (
                  <div className="flex items-center space-x-2">
                    <input
                      type="text"
                      value={tagValue}
                      onChange={(e) => setTagValue(e.target.value)}
                      className="px-3 py-1 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                      placeholder="Enter tag"
                      maxLength={50}
                    />
                    <button
                      onClick={handleTagSave}
                      disabled={isUpdating}
                      className="p-1 text-green-600 hover:text-green-800 disabled:opacity-50"
                      title="Save tag"
                    >
                      <Save className="h-4 w-4" />
                    </button>
                    <button
                      onClick={handleTagCancel}
                      disabled={isUpdating}
                      className="p-1 text-gray-500 hover:text-gray-700 disabled:opacity-50"
                      title="Cancel"
                    >
                      <X className="h-4 w-4" />
                    </button>
                  </div>
                ) : (
                  <div className="flex items-center space-x-2">
                    <span className="font-semibold text-gray-900">{userFundDTO?.tag || 'No tag'}</span>
                    <button
                      onClick={handleTagEdit}
                      disabled={isUpdating}
                      className="p-1 text-blue-600 hover:text-blue-800 disabled:opacity-50"
                      title={userFundDTO?.tag ? "Edit tag" : "Add tag"}
                    >
                      <Edit3 className="h-4 w-4" />
                    </button>
                  </div>
                )}
              </div>
              <div className="flex justify-between items-center p-3 bg-gradient-to-r from-gray-50 to-gray-100 rounded-lg">
                <span className="text-gray-600 font-medium">Emergency Fund:</span>
                <div className="flex items-center space-x-3">
                  <span className={`font-semibold flex items-center ${
                    userFundDTO?.isEmergency ? 'text-red-600' : 'text-green-600'
                  }`}>
                    {userFundDTO?.isEmergency ? (
                      <>
                        <Shield className="w-4 h-4 mr-1" />
                        Yes
                      </>
                    ) : (
                      'No'
                    )}
                  </span>
                  <button
                    onClick={handleEmergencyToggle}
                    disabled={isUpdating}
                    className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50 ${
                      userFundDTO?.isEmergency ? 'bg-red-600' : 'bg-gray-200'
                    }`}
                    title={`${userFundDTO?.isEmergency ? 'Remove from' : 'Mark as'} emergency fund`}
                  >
                    <span
                      className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${
                        userFundDTO?.isEmergency ? 'translate-x-6' : 'translate-x-1'
                      }`}
                    />
                  </button>
                </div>
              </div>
              <div className="flex justify-between items-center p-3 bg-gradient-to-r from-gray-50 to-gray-100 rounded-lg">
                <span className="text-gray-600 font-medium">Total Units:</span>
                <span className="font-semibold text-gray-900">
                  {summary?.totalUnits != null ? summary.totalUnits.toFixed(4) : '0.0000'}
                </span>
              </div>
            </div>
          </div>

          {/* Registered SIPs */}
          <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-200">
            <div className="flex items-center mb-6">
              <Calendar className="w-6 h-6 mr-3 text-purple-600" />
              <h3 className="text-xl font-bold text-gray-900">Registered SIPs</h3>
            </div>
            {registeredSIPs && registeredSIPs.length > 0 ? (
              <div className="space-y-3">
                {registeredSIPs.map((sip, index) => (
                  <div key={sip.id || index} className="flex justify-between items-center p-4 bg-gradient-to-r from-purple-50 to-indigo-50 rounded-lg hover:shadow-md transition-all duration-300">
                    <div>
                      <p className="font-semibold text-gray-900">{sip.fundName}</p>
                      <p className="text-sm text-gray-600 flex items-center">
                        <Zap className="w-3 h-3 mr-1 text-yellow-500" />
                        Monthly SIP
                      </p>
                    </div>
                    <div className="text-right">
                      <p className="font-bold text-gray-900">{formatCurrency(sip.amount)}</p>
                      <p className="text-sm text-gray-600">per month</p>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center py-12">
                <div className="w-16 h-16 bg-gradient-to-br from-purple-100 to-indigo-100 rounded-full flex items-center justify-center mx-auto mb-4">
                  <Calendar className="h-8 w-8 text-purple-500" />
                </div>
                <p className="text-gray-500 font-medium">No SIPs registered</p>
                <p className="text-sm text-gray-400 mt-2">Click "Register SIP" to set up a new SIP</p>
              </div>
            )}
          </div>
        </div>

        {/* Enhanced Units History */}
        <div className="bg-white rounded-xl shadow-lg border border-gray-200 overflow-hidden">
          <div className="px-6 py-4 border-b border-gray-200 bg-gradient-to-r from-blue-50 to-indigo-50">
            <div className="flex items-center justify-between">
              <h3 className="text-xl font-bold text-gray-900 flex items-center">
                <PieChart className="h-6 w-6 mr-3 text-blue-600" />
                Units History
              </h3>
              <div className="flex space-x-2">
                <div className="flex items-center space-x-1 text-xs text-gray-600">
                  <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse"></div>
                  <span>Active BUY</span>
                </div>
                <div className="flex items-center space-x-1 text-xs text-gray-600">
                  <div className="w-3 h-3 bg-gray-500 rounded-full"></div>
                  <span>Sold BUY</span>
                </div>
                <div className="flex items-center space-x-1 text-xs text-gray-600">
                  <div className="w-3 h-3 bg-red-500 rounded-full animate-pulse"></div>
                  <span>SELL</span>
                </div>
              </div>
            </div>
          </div>
          <div className="overflow-x-auto">
            {units && units.length > 0 ? (
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Date
                    </th>
                    <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Transaction Type
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Amount
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Units
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      P&L
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      P&L %
                    </th>
                    <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Actions
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {units.map((unit, index) => (
                    <UnitsRow
                      key={index}
                      unit={unit}
                      index={index}
                      formatCurrency={formatCurrency}
                      formatDate={formatDate}
                      formatPercentage={formatPercentage}
                      onDelete={handleDeleteTransaction}
                    />
                  ))}
                </tbody>
              </table>
            ) : (
              <div className="text-center py-16">
                <div className="w-20 h-20 bg-gradient-to-br from-blue-100 to-purple-100 rounded-full flex items-center justify-center mx-auto mb-6">
                  <PieChart className="w-10 h-10 text-blue-500" />
                </div>
                <p className="text-gray-500 font-medium">No units history available</p>
                <p className="text-sm text-gray-400 mt-2">Record transactions to see units history</p>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Forms */}
      <TransactionForm
        isOpen={showTransactionForm}
        onClose={() => setShowTransactionForm(false)}
        fund={fund}
        userId={userId}
        onSuccess={handleSuccess}
      />
      <BulkTransactionForm
        isOpen={showBulkTransactionForm}
        onClose={() => setShowBulkTransactionForm(false)}
        fund={fund}
        userId={userId}
        onSuccess={handleSuccess}
      />
      <SIPForm
        isOpen={showSIPForm}
        onClose={() => setShowSIPForm(false)}
        fund={fund}
        userId={userId}
        onSuccess={handleSuccess}
      />

      {/* Delete Transaction Confirmation Dialog */}
      {showDeleteDialog && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3 text-center">
              {/* Icon */}
              <div className="mx-auto flex items-center justify-center h-12 w-12 rounded-full bg-red-100">
                <svg 
                  className="h-6 w-6 text-red-600" 
                  fill="none" 
                  stroke="currentColor" 
                  viewBox="0 0 24 24"
                >
                  <path 
                    strokeLinecap="round" 
                    strokeLinejoin="round" 
                    strokeWidth={2} 
                    d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" 
                  />
                </svg>
              </div>

              {/* Title */}
              <h3 className="text-lg font-medium text-gray-900 mt-4">
                Delete Transaction
              </h3>

              {/* Message */}
              <div className="mt-2 px-7 py-3">
                {transactionToDelete && (
                  <div className="text-sm text-gray-500 space-y-2">
                    <p>Are you sure you want to delete this transaction?</p>
                    <div className="bg-gray-50 p-3 rounded-lg text-left">
                      <p><span className="font-medium">Date:</span> {formatDate(transactionToDelete.date)}</p>
                      <p><span className="font-medium">Amount:</span> {formatCurrency(transactionToDelete.amount)}</p>
                      <p><span className="font-medium">Units:</span> {transactionToDelete.units?.toFixed(4) || '0.0000'}</p>
                    </div>
                    <p className="text-red-600 font-medium">This action cannot be undone.</p>
                  </div>
                )}
              </div>

              {/* Error Message */}
              {dialogError && (
                <div className="mt-4 px-7 py-4 bg-red-50 border-l-4 border-red-400 rounded-lg animate-fade-in">
                  <div className="flex items-start">
                    <div className="flex-shrink-0">
                      <svg className="h-6 w-6 text-red-400 mt-0.5" viewBox="0 0 20 20" fill="currentColor">
                        <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                      </svg>
                    </div>
                    <div className="ml-3 flex-1">
                      <h4 className="text-sm font-medium text-red-800 mb-1">Deletion Failed</h4>
                      <p className="text-sm text-red-700 leading-relaxed">{dialogError}</p>
                      {dialogError.includes('Redeemed Transaction') && (
                        <div className="mt-2 p-2 bg-red-100 rounded text-xs text-red-600">
                           <strong>Tip:</strong> Redeemed transactions cannot be deleted as they have already been sold and processed.
                        </div>
                      )}
                    </div>
                    <div className="ml-3">
                      <button
                        onClick={() => setDialogError(null)}
                        className="text-red-400 hover:text-red-600 focus:outline-none transition-colors duration-200"
                        title="Dismiss error"
                      >
                        <svg className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                          <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
                        </svg>
                      </button>
                    </div>
                  </div>
                </div>
              )}

              {/* Buttons */}
              <div className="flex justify-center space-x-3 mt-4">
                <button
                  onClick={cancelDeleteTransaction}
                  disabled={isDeleting}
                  className="px-4 py-2 bg-gray-300 text-gray-700 text-base font-medium rounded-md shadow-sm hover:bg-gray-400 focus:outline-none focus:ring-2 focus:ring-gray-300 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Cancel
                </button>
                <button
                  onClick={confirmDeleteTransaction}
                  disabled={isDeleting}
                  className="px-4 py-2 bg-red-600 text-white text-base font-medium rounded-md shadow-sm hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {isDeleting ? (
                    <div className="flex items-center">
                      <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" fill="none" viewBox="0 0 24 24">
                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                      </svg>
                      Deleting...
                    </div>
                  ) : (
                    'Delete Transaction'
                  )}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Historic Data Modal */}
      {showHistoricDataModal && (
        <HistoricDataModal
          userId={userId}
          fundId={fund.schemeCode || fund.fundId}
          title={`Historic Data - ${fund.schemeName || fund.name}`}
          onClose={() => setShowHistoricDataModal(false)}
          fetchData={userFundAPI.getUserFundHistoricData}
        />
      )}
    </div>
  );
};

export default UserFundDetail;
