import React, { useState } from 'react';
import { User, UserCircle, Key, Save, X } from 'lucide-react';
import { userAPI } from '../../services/api';

const Profile = ({ user, onUpdate }) => {
  const [name, setName] = useState(user?.name || '');
  const [username, setUsername] = useState(user?.loginId || '');
  const [password, setPassword] = useState('');
  const [isUpdating, setIsUpdating] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsUpdating(true);
    setErrorMessage('');
    setSuccessMessage('');

    try {
      const updateData = {};

      if (name && name !== user.name) {
        updateData.name = name;
      }

      if (username && username !== user.loginId) {
        updateData.loginId = username;
      }

      if (password) {
        updateData.password = password;
      }

      if (Object.keys(updateData).length === 0) {
        setErrorMessage('No changes to update');
        setIsUpdating(false);
        return;
      }

      await userAPI.updateUser(user.id, updateData);

      setSuccessMessage('Profile updated successfully');
      setTimeout(() => setSuccessMessage(''), 3000);

      // Update the user in the parent component
      if (onUpdate) {
        onUpdate({ ...user, ...updateData });
      }

      // Clear password field
      setPassword('');
    } catch (error) {
      console.error('Error updating profile:', error);
      setErrorMessage(error.response?.data?.message || 'Failed to update profile. Please try again.');
    } finally {
      setIsUpdating(false);
    }
  };

  const handleReset = () => {
    setName(user?.name || '');
    setUsername(user?.loginId || '');
    setPassword('');
    setErrorMessage('');
    setSuccessMessage('');
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <div className="flex items-center space-x-4">
          <div className="p-3 bg-blue-100 rounded-lg">
            <User className="h-6 w-6 text-blue-600" />
          </div>
          <div>
            <h2 className="text-2xl font-bold text-gray-900">Profile Settings</h2>
            <p className="text-sm text-gray-500">Manage your account information and preferences</p>
          </div>
        </div>
      </div>

      {/* Messages */}
      {successMessage && (
        <div className="bg-green-50 border border-green-200 rounded-lg p-4 flex items-center justify-between">
          <p className="text-sm font-medium text-green-800">{successMessage}</p>
          <button onClick={() => setSuccessMessage('')} className="text-green-600 hover:text-green-800">
            <X className="h-4 w-4" />
          </button>
        </div>
      )}
      {errorMessage && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-4 flex items-center justify-between">
          <p className="text-sm font-medium text-red-800">{errorMessage}</p>
          <button onClick={() => setErrorMessage('')} className="text-red-600 hover:text-red-800">
            <X className="h-4 w-4" />
          </button>
        </div>
      )}

      {/* Profile Form */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200">
        <div className="px-6 py-4 border-b border-gray-200">
          <h3 className="text-lg font-semibold text-gray-900">Personal Information</h3>
        </div>
        <form onSubmit={handleSubmit} className="p-6 space-y-6">
          {/* Name Field */}
          <div>
            <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-2">
              <div className="flex items-center space-x-2">
                <UserCircle className="h-4 w-4 text-gray-500" />
                <span>Full Name</span>
              </div>
            </label>
            <input
              type="text"
              id="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="block w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 text-gray-900"
              placeholder="Enter your full name"
            />
          </div>

          {/* Username Field */}
          <div>
            <label htmlFor="username" className="block text-sm font-medium text-gray-700 mb-2">
              <div className="flex items-center space-x-2">
                <User className="h-4 w-4 text-gray-500" />
                <span>Username</span>
              </div>
            </label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="block w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 text-gray-900"
              placeholder="Enter your username"
            />
          </div>

          {/* Password Field */}
          <div>
            <label htmlFor="password" className="block text-sm font-medium text-gray-700 mb-2">
              <div className="flex items-center space-x-2">
                <Key className="h-4 w-4 text-gray-500" />
                <span>New Password</span>
              </div>
            </label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="block w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 text-gray-900"
              placeholder="Enter new password (leave empty to keep current)"
            />
            <p className="mt-1 text-xs text-gray-500">Leave empty if you don't want to change your password</p>
          </div>

          {/* Action Buttons */}
          <div className="flex items-center space-x-4 pt-4">
            <button
              type="submit"
              disabled={isUpdating}
              className="flex items-center space-x-2 px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-lg shadow-md transition-all duration-200 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <Save className="h-4 w-4" />
              <span>{isUpdating ? 'Updating...' : 'Update Profile'}</span>
            </button>
            <button
              type="button"
              onClick={handleReset}
              disabled={isUpdating}
              className="flex items-center space-x-2 px-6 py-3 bg-gray-100 hover:bg-gray-200 text-gray-700 font-semibold rounded-lg transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <X className="h-4 w-4" />
              <span>Reset</span>
            </button>
          </div>
        </form>
      </div>

      {/* Current User Info Display */}
      <div className="bg-gray-50 rounded-lg border border-gray-200 p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Account Information</h3>
        <div className="space-y-3">
          <div className="flex items-center justify-between py-2 border-b border-gray-200">
            <span className="text-sm text-gray-500">User ID</span>
            <span className="text-sm font-medium text-gray-900">{user?.id}</span>
          </div>
          <div className="flex items-center justify-between py-2 border-b border-gray-200">
            <span className="text-sm text-gray-500">Current Username</span>
            <span className="text-sm font-medium text-gray-900">{user?.loginId}</span>
          </div>
          <div className="flex items-center justify-between py-2">
            <span className="text-sm text-gray-500">Current Name</span>
            <span className="text-sm font-medium text-gray-900">{user?.name}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
