import React from 'react';
import { 
  TrendingUp, 
  TrendingDown, 
  DollarSign, 
  Target, 
  Shield, 
  PieChart, 
  Calendar,
  Award,
  BarChart3,
  Activity,
  Zap
} from 'lucide-react';

const PortfolioSummary = ({ dashboardData }) => {
  const isProfit = dashboardData.profitLoss >= 0;
  const profitLossColor = isProfit ? 'text-green-600' : 'text-red-600';
  const profitLossBgColor = isProfit ? 'bg-green-50' : 'bg-red-50';

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount || 0);
  };

  const formatPercentage = (percentage) => {
    if (percentage == null || isNaN(percentage)) {
      return 'N/A';
    }
    return `${percentage >= 0 ? '+' : ''}${percentage.toFixed(2)}%`;
  };

  const formatXIRR = (xirr) => {
    if (xirr == null || isNaN(xirr)) {
      return 'N/A';
    }
    return `${xirr.toFixed(2)}%`;
  };

  // Calculate additional metrics
  const totalFunds = dashboardData.fundSummaries?.length || 0;
  const emergencyFunds = dashboardData.fundSummaries?.filter(fund => fund.isEmergency) || [];
  const emergencyFundCount = emergencyFunds.length;
  const totalRealizedProfit = dashboardData.fundSummaries?.reduce((sum, fund) => sum + (fund.totalRealizedProfit || 0), 0) || 0;
  const currentYearRealizedProfit = dashboardData.fundSummaries?.reduce((sum, fund) => sum + (fund.currentYearTotalRealizedProfit || 0), 0) || 0;

  const mainMetrics = [
    {
      title: 'Total Invested',
      value: formatCurrency(dashboardData.totalInvested),
      icon: DollarSign,
      color: 'text-blue-600',
      bgColor: 'bg-gradient-to-br from-blue-50 to-blue-100',
      iconBg: 'bg-blue-500',
      description: 'Total amount invested across all funds'
    },
    {
      title: 'Current Value',
      value: formatCurrency(dashboardData.totalValue),
      icon: Target,
      color: 'text-purple-600',
      bgColor: 'bg-gradient-to-br from-purple-50 to-purple-100',
      iconBg: 'bg-purple-500',
      description: 'Current market value of your portfolio'
    },
    {
      title: 'Unrealized P&L',
      value: formatCurrency(Math.abs(dashboardData.profitLoss)),
      percentage: formatPercentage(dashboardData.profitLossPercent),
      icon: isProfit ? TrendingUp : TrendingDown,
      color: profitLossColor,
      bgColor: isProfit ? 'bg-gradient-to-br from-green-50 to-green-100' : 'bg-gradient-to-br from-red-50 to-red-100',
      iconBg: isProfit ? 'bg-green-500' : 'bg-red-500',
      description: 'Current unrealized profit/loss'
    },
    {
      title: 'XIRR',
      value: formatXIRR(dashboardData.xirr),
      icon: Activity,
      color: 'text-indigo-600',
      bgColor: 'bg-gradient-to-br from-indigo-50 to-indigo-100',
      iconBg: 'bg-indigo-500',
      description: 'Extended Internal Rate of Return'
    }
  ];

  const additionalMetrics = [
    {
      title: 'Realized Profit',
      value: formatCurrency(totalRealizedProfit),
      icon: Award,
      color: 'text-emerald-600',
      bgColor: 'bg-gradient-to-br from-emerald-50 to-emerald-100',
      iconBg: 'bg-emerald-500',
      description: 'Total profit from sold investments'
    },
    {
      title: 'Current Year Realized',
      value: formatCurrency(currentYearRealizedProfit),
      icon: Calendar,
      color: 'text-orange-600',
      bgColor: 'bg-gradient-to-br from-orange-50 to-orange-100',
      iconBg: 'bg-orange-500',
      description: 'Realized profit this financial year'
    },
    {
      title: 'Long Term Gains',
      value: formatCurrency(dashboardData.longTermGains),
      icon: TrendingUp,
      color: 'text-violet-600',
      bgColor: 'bg-gradient-to-br from-violet-50 to-violet-100',
      iconBg: 'bg-violet-500',
      description: 'Long term capital gains from investments'
    },
    {
      title: 'Emergency Fund',
      value: formatCurrency(dashboardData.totalEmergencyFundValue),
      icon: Shield,
      color: 'text-yellow-600',
      bgColor: 'bg-gradient-to-br from-yellow-50 to-yellow-100',
      iconBg: 'bg-yellow-500',
      description: 'Value of emergency fund investments'
    },
    {
      title: 'Total Funds',
      value: totalFunds.toString(),
      icon: PieChart,
      color: 'text-cyan-600',
      bgColor: 'bg-gradient-to-br from-cyan-50 to-cyan-100',
      iconBg: 'bg-cyan-500',
      description: 'Number of funds in your portfolio'
    }
  ];

  return (
    <div className="space-y-6">
      {/* Main Portfolio Overview */}
      <div className="bg-white rounded-xl shadow-lg border border-gray-200 overflow-hidden">
        <div className="bg-gradient-to-r from-blue-600 to-purple-600 px-6 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-2xl font-bold text-white">Portfolio Overview</h2>
              <p className="text-blue-100">Welcome back, {dashboardData.userName}!</p>
            </div>
            <div className="flex items-center space-x-2">
              <div className="p-2 bg-white bg-opacity-20 rounded-lg">
                <BarChart3 className="w-6 h-6 text-white" />
              </div>
            </div>
          </div>
        </div>

        {/* Main Metrics Grid */}
        <div className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {mainMetrics.map((metric, index) => (
              <div key={index} className={`p-6 rounded-xl ${metric.bgColor} hover:shadow-lg transition-all duration-300 hover:scale-105 group`}>
                <div className="flex items-center justify-between mb-4">
                  <div className={`p-3 rounded-lg ${metric.iconBg} group-hover:scale-110 transition-transform duration-300`}>
                    <metric.icon className="w-6 h-6 text-white" />
                  </div>
                  <div className="text-right">
                    <p className="text-sm font-medium text-gray-600 mb-1">{metric.title}</p>
                    <p className={`text-2xl font-bold ${metric.color}`}>{metric.value}</p>
                    {metric.percentage && (
                      <p className={`text-sm font-medium ${metric.color} mt-1`}>
                        {metric.percentage}
                      </p>
                    )}
                  </div>
                </div>
                <p className="text-xs text-gray-500">{metric.description}</p>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Additional Metrics */}
      <div className="bg-white rounded-xl shadow-lg border border-gray-200 overflow-hidden">
        <div className="bg-gradient-to-r from-gray-50 to-gray-100 px-6 py-4 border-b border-gray-200">
          <h3 className="text-lg font-semibold text-gray-900 flex items-center">
            <Zap className="w-5 h-5 mr-2 text-gray-600" />
            Performance Metrics
          </h3>
        </div>

        <div className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {additionalMetrics.map((metric, index) => (
              <div key={index} className={`p-4 rounded-lg ${metric.bgColor} hover:shadow-md transition-all duration-300 group`}>
                <div className="flex items-center justify-between">
                  <div className={`p-2 rounded-lg ${metric.iconBg} group-hover:scale-110 transition-transform duration-300`}>
                    <metric.icon className="w-5 h-5 text-white" />
                  </div>
                  <div className="text-right">
                    <p className="text-sm font-medium text-gray-600">{metric.title}</p>
                    <p className={`text-lg font-bold ${metric.color}`}>{metric.value}</p>
                  </div>
                </div>
                <p className="text-xs text-gray-500 mt-2">{metric.description}</p>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Emergency Fund Details */}
      {emergencyFundCount > 0 && (
        <div className="bg-white rounded-xl shadow-lg border border-gray-200 overflow-hidden">
          <div className="bg-gradient-to-r from-yellow-50 to-orange-50 px-6 py-4 border-b border-gray-200">
            <h3 className="text-lg font-semibold text-gray-900 flex items-center">
              <Shield className="w-5 h-5 mr-2 text-yellow-600" />
              Emergency Fund Details
            </h3>
          </div>

          <div className="p-6">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="bg-gradient-to-br from-yellow-50 to-yellow-100 p-4 rounded-lg">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-600">Emergency Funds</p>
                    <p className="text-2xl font-bold text-yellow-600">{emergencyFundCount}</p>
                  </div>
                  <Shield className="w-8 h-8 text-yellow-500" />
                </div>
                <p className="text-xs text-gray-500 mt-2">Number of emergency funds</p>
              </div>

              <div className="bg-gradient-to-br from-orange-50 to-orange-100 p-4 rounded-lg">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-600">Total Value</p>
                    <p className="text-2xl font-bold text-orange-600">
                      {formatCurrency(dashboardData.totalEmergencyFundValue)}
                    </p>
                  </div>
                  <DollarSign className="w-8 h-8 text-orange-500" />
                </div>
                <p className="text-xs text-gray-500 mt-2">Emergency fund value</p>
              </div>

              <div className="bg-gradient-to-br from-red-50 to-red-100 p-4 rounded-lg">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-600">Portfolio %</p>
                    <p className="text-2xl font-bold text-red-600">
                      {dashboardData.totalValue > 0 ? 
                        ((dashboardData.totalEmergencyFundValue / dashboardData.totalValue) * 100).toFixed(1) + '%' : 
                        '0%'
                      }
                    </p>
                  </div>
                  <PieChart className="w-8 h-8 text-red-500" />
                </div>
                <p className="text-xs text-gray-500 mt-2">Emergency fund allocation</p>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PortfolioSummary;
