import React, { useState, useEffect } from 'react';
import { X } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, Brush } from 'recharts';
import LoadingSpinner from '../common/LoadingSpinner.jsx';

const NavHistoryModal = ({ userId, fundId, title, onClose, fetchData }) => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);
        setError(null);
        const response = await fetchData(userId, fundId);
        console.log('NAV History Data Response:', response);

        // Check if response is an array
        if (Array.isArray(response)) {
          setData(response);
        } else if (response && typeof response === 'object') {
          // If it's an object, try to extract the array from common properties
          const dataArray = response.data || response.chartData || response.historicData || response.navHistory || Object.values(response)[0] || [];
          setData(Array.isArray(dataArray) ? dataArray : []);
        } else {
          setData([]);
        }
      } catch (err) {
        console.error('Error loading NAV history:', err);
        setError('Failed to load NAV history');
        setData([]);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, [userId, fundId, fetchData]);

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }).format(amount || 0);
  };

  const formatDate = (dateString) => {
    if (!dateString) return '';
    try {
      return new Date(dateString).toLocaleDateString('en-IN', { 
        year: 'numeric', 
        month: 'short', 
        day: 'numeric' 
      });
    } catch {
      return dateString;
    }
  };

  if (loading) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-white rounded-xl shadow-2xl max-w-7xl w-full max-h-[90vh] flex items-center justify-center p-8">
          <LoadingSpinner />
        </div>
      </div>
    );
  }

  if (error || !data || data.length === 0) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-white rounded-xl shadow-2xl max-w-7xl w-full max-h-[90vh] p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-2xl font-bold text-gray-900">{title}</h2>
            <button
              onClick={onClose}
              className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-all duration-200"
            >
              <X className="w-6 h-6" />
            </button>
          </div>
          <div className="text-center py-12">
            <p className="text-gray-600">{error || 'No NAV history available'}</p>
          </div>
        </div>
      </div>
    );
  }

  // Determine the data key for NAV value
  const navKey = data[0] ? Object.keys(data[0]).find(key => 
    key.toLowerCase().includes('nav') || 
    key.toLowerCase().includes('value') ||
    key.toLowerCase().includes('price')
  ) : null;

  const dateKey = data[0] ? Object.keys(data[0]).find(key => 
    key.toLowerCase().includes('date') || 
    key.toLowerCase().includes('time')
  ) : null;

  if (!navKey) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-white rounded-xl shadow-2xl max-w-7xl w-full max-h-[90vh] p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-2xl font-bold text-gray-900">{title}</h2>
            <button
              onClick={onClose}
              className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-all duration-200"
            >
              <X className="w-6 h-6" />
            </button>
          </div>
          <div className="text-center py-12">
            <p className="text-gray-600">Unable to determine NAV field from data</p>
            <pre className="mt-4 text-xs text-left bg-gray-100 p-4 rounded overflow-auto">
              {JSON.stringify(data.slice(0, 2), null, 2)}
            </pre>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-2xl max-w-7xl w-full max-h-[90vh] flex flex-col">
        {/* Header */}
        <div className="flex items-center justify-between px-6 py-4 border-b border-gray-200">
          <h2 className="text-2xl font-bold text-gray-900">{title}</h2>
          <button
            onClick={onClose}
            className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-all duration-200"
          >
            <X className="w-6 h-6" />
          </button>
        </div>

        {/* Content */}
        <div className="flex-1 overflow-y-auto p-6">
          <div className="bg-white rounded-lg border border-gray-200 p-6">
            <h3 className="text-lg font-bold text-gray-900 mb-4">NAV History</h3>
            <ResponsiveContainer width="100%" height={600}>
              <LineChart data={data} margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis 
                  dataKey={dateKey || navKey} 
                  tickFormatter={(value) => formatDate(value)}
                />
                <YAxis tickFormatter={(value) => `₹${value.toFixed(2)}`} />
                <Tooltip 
                  content={({ active, payload }) => {
                    if (!active || !payload || !payload.length) return null;

                    return (
                      <div className="bg-white border border-gray-300 rounded-lg shadow-lg p-3">
                        {payload.map((entry, index) => (
                          <p key={index} style={{ color: entry.color }}>
                            {`${entry.name}: ₹${entry.value?.toFixed(2) || '0.00'}`}
                          </p>
                        ))}
                      </div>
                    );
                  }}
                />
                <Legend />
                <Line 
                  type="monotone" 
                  dataKey={navKey} 
                  stroke="#8884d8" 
                  strokeWidth={2} 
                  dot={{ fill: '#8884d8', r: 3 }} 
                  activeDot={{ r: 6 }} 
                  name="NAV" 
                />
                <Brush dataKey={dateKey || navKey} height={30} stroke="#8884d8" />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NavHistoryModal;
