import React from 'react';

const DashboardNavigation = ({ currentView, onViewChange, onLogout, user }) => {
  return (
    <nav className="bg-white/90 backdrop-blur supports-backdrop-blur:bg-white/60 shadow-sm border-b border-gray-200">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          {/* Brand */}
          <div className="flex items-center">
            <div className="flex-shrink-0 select-none">
              <span className="text-2xl sm:text-3xl font-extrabold tracking-tight bg-gradient-to-r from-blue-600 via-purple-600 to-indigo-600 bg-clip-text text-transparent drop-shadow-sm">
                Stonks
              </span>
            </div>
          </div>

          {/* Right actions: Welcome, Profile, Logout */}
          <div className="flex items-center space-x-3">
            <div className="hidden sm:flex flex-col items-start">
              <div className="text-sm text-gray-700">
                Welcome, <span className="font-medium">{user.name}</span>
              </div>
              <button
                onClick={() => onViewChange && onViewChange('profile')}
                className="mt-1 px-2 py-1 rounded text-xs font-medium text-gray-700 bg-gray-100 hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-300"
              >
                Profile
              </button>
            </div>
            <button
              onClick={onLogout}
              className="px-4 py-2 rounded-md text-sm font-medium text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2"
            >
              Logout
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default DashboardNavigation;
