import axios from 'axios';
// import { UserDashboardDTO, MutualFundDTO } from '../types/index.js';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8081';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor for logging
api.interceptors.request.use(
  (config) => {
    console.log(`Making ${config.method?.toUpperCase()} request to ${config.url}`);
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor for error handling
api.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    console.error('API Error:', error.response?.data || error.message);
    return Promise.reject(error);
  }
);

export const dashboardAPI = {
  // Get user dashboard data
  getUserDashboard: async (userId) => {
    try {
      const response = await api.get(`/api/dashboard?userId=${userId}`);
      return response.data || null;
    } catch (error) {
      console.error('Error fetching user dashboard:', error);
      throw error;
    }
  },

  // Search for mutual funds
  searchFunds: async (searchText: string): Promise<MutualFundDTO[]> => {
    try {
      const response = await api.get(`/api/dashboard/searchFund?searchText=${encodeURIComponent(searchText)}`);
      return response.data || [];
    } catch (error) {
      console.error('Error searching funds:', error);
      throw error;
    }
  },
};

export default api;
